(147, 'Interlibrary Loan', 'You can request items that Drew&#39;s Library does not have through ILLiad, Drew&#39;s Interlibrary Loan system. Though we boast impressive average request times, please give our ILL staff plenty of time to find your material.&nbsp;\n<ul>\n	<li><a href="http://illiad.drew.edu">ILLiad </a>(requires your Drew userid &amp; password)</li>\n</ul>\n', NULL, 0, 'Basic', '', 0, 0, NULL),
(148, 'Email yourself an article', '<img alt="" src="http://walter.drew.edu/splus/assets/users/_epatters/email_yourself.png" style="width: 945px; height: 155px;" /><br />\n&nbsp;', NULL, 0, 'Basic', '', 0, 0, NULL),
(149, 'Thinking about your  topic', '<em><strong>Find a scholarly article on the topic of superfunds, class and environmental justice<br />\n<br />\nAre there other words that describe your topic? &nbsp;or synonyms?&nbsp;</strong></em>\n<ul>\n	<li><em><strong>superfunds</strong></em></li>\n	<li><em><strong>class, minorities, ethnicities, poor, poverty, rich, middle class</strong></em></li>\n	<li><em><strong>environmental justice, justice</strong></em></li>\n</ul>\n<em><strong>Searching tips.</strong></em>\n\n<ul>\n	<li>Use separate search boxes for separate concepts/keywords</li>\n	<li>Use an * to capture all endings of a word. Example: ethnic* = ethnic, ethnicity, ethnicities</li>\n	<li>Use quotation marks (&quot;&quot;) around words you want to keep as a phrase. Example: &quot;environmental justice*&quot;</li>\n	<li>Use upper case OR to string synonyms together</li>\n</ul>\n', NULL, 0, 'Basic', '', 0, 0, NULL),
