(156, 'Background', '<strong>Primary vs Secondary</strong><br />\n{{dab},{1273}, {Identifying primary sources in the sciences},{11}}<br />\n<a href="https://drive.google.com/file/d/0B08KJ0ZLTR8SeDRGcnRUc2FXMVE/view?usp=sharing">article 1</a><br />\n<a href="https://drive.google.com/file/d/0B08KJ0ZLTR8SSmVfTXJqVUxvazA/view?usp=sharing">article 2</a><br />\n<br />\n<strong>General</strong>\n<ul>\n	<li>{{dab},{51}, {Credo Reference},{11}}</li>\n	<li>{{dab},{1053}, {Merck Index, 14th edition, Print},{11}}</li>\n</ul>\n\n<h5 id="id-2014Summer-CHEM250LPersonalCareProductLab-ChemicalConstituentsofNaturalIngredientsinPersonalCareProducts" style="margin: 20px 0px 0px; padding: 0px; color: rgb(61, 61, 61); font-size: 14px; line-height: 20px; font-family: Arial, sans-serif; background-color: rgb(255, 255, 255);"><strong>Chemical Constituents of Natural Ingredients in Personal Care Products</strong></h5>\n\n<ul>\n	<li>{{dab},{1054}, {Handbook of Clinically Tested Herbal Remedies},{11}}</li>\n</ul>\n&nbsp;\n\n<ul>\n	<li>{{dab},{1055}, {Evidence-Based Herbal Medicine},{11}}</li>\n</ul>\n&nbsp;\n\n<ul>\n	<li>{{dab},{1056}, {Mosby&#39;s Handbook of Herbs &amp; Natural Substances},{11}}</li>\n</ul>\n&nbsp;\n\n<ul>\n	<li>{{dab},{1057}, {Physicians&#39; Desk Reference for Nonprescription Drugs, Dietary Supplements, and Herbs},{11}}</li>\n</ul>\n', NULL, 0, 'Basic', '', 0, 0, NULL),
(157, 'Recommended websites', '<strong>Chemical structure</strong>\n<ul>\n	<li>{{dab},{1300}, {ChemIDplus},{11}}</li>\n	<li>{{dab},{628}, {ChemSpider},{11}}</li>\n	<li>{{dab},{632}, { PubChem Project},{11}}</li>\n</ul>\n<strong>General personal product information with specific data</strong>\n\n<ul>\n	<li>{{dab},{1303}, {Cosmeticsinfo.org &quot;What&#39;s in my products?&quot;},{11}}</li>\n	<li>{{dab},{1299}, {EWG&#39;s Skin Deep (TM). Cosmetics database},{11}}</li>\n	<li>{{dab},{897}, {Household products database},{11}}</li>\n</ul>\n<strong>Hazardous substance information</strong>\n\n<ul>\n	<li>{{dab},{596}, {ACToR},{11}}</li>\n	<li>{{dab},{1211}, {NIOSH Pocket Guide to Chemical Hazards},{11}}</li>\n	<li>{{dab},{623}, {TOXNET},{11}}</li>\n</ul>\n<strong>Functional groups:</strong>\n\n<ul>\n	<li>From UC-Davis ChemWiki&nbsp;<a href="http://chemwiki.ucdavis.edu/Organic_Chemistry/Organic_Chemistry_With_a_Biological_Emphasis/Reference_Tables/Examples_of_common_functional_groups_in_organic_chemistry">Examples_of_common_functional_groups_in_organic_chemistry</a></li>\n</ul>\n', NULL, 0, 'Basic', '', 0, 0, NULL),
(159, 'Databases', '{{dab},{217}, {American Chemical Society Journals},{11}}<br />\n<br />\n{{dab},{18}, {Google Scholar},{11}}<br />\n<br />\n{{dab},{29}, {ScienceDirect},{11}}<br />\n<br />\nStudents have access to a core group of journals to which Drew University subscribes. For unsubscribed content, students may&nbsp;request articles outside of our subscriptions through&nbsp;either the Library CHAT function&nbsp;&nbsp;when staffed (fall semester hours: M-Fri 9-5PM; Sun-Thurs 6-10PM)&nbsp;and&nbsp;found on this and the Library Home page,&nbsp;or by requesting copies through the Interlibrary Loan system (<a href="https://illiad.drew.edu/illiad/Logon.html">illiad.drew.edu</a>). &nbsp;Either way, students will be supplied with an electronic copy of the journal article. &nbsp;Go&nbsp;<a href="https://uknow.drew.edu/confluence/pages/viewpage.action?pageId=43909305">HERE</a>&nbsp;for more information about obtaining ScienceDirect articles.<br />\n<br />\n{{dab},{1123}, {InterLibrary Loan},{11}}<br />\n<br />\nTo request a <strong>ScienceDirect</strong> article through Inter-library loan:&nbsp;Click on the &quot;Search For Article&quot; button on the article in ScienceDirect&nbsp;<br />\nThen, click &ldquo;Request through InterLibrary Loan&rdquo; on the bottom of the following screen.<br />\nSign into your ILLiad &nbsp;account and the article information will automatically fill in the appropriate spots in the form.<br />\n&nbsp;Type &#39;0&#39; in the spot marked &ldquo;Max you are willing to pay&rdquo;<br />\nClick &ldquo;Submit request&rdquo;<br />\nInterlibrary Loan will then download and supply the articles through ILLiad.&nbsp;.You should receive the article in 24 - 72 hours.<br />\n<br />\n{{dab},{80}, {Web of Science and Knowledge (ISI Citation Indexes)},{11}}<br />\nWeb of Science<br />\nTopic searching searches title, abstract. To limit further, try limiting to title.<br />\n<br />\nWithin searches you can limit within topic (as decided by journal title), author, affiliation, year.<br />\nCan analyze results, get related (articles with citations in common with the source article).&nbsp;Biological Abstracts<br />\n<br />\n&nbsp; &nbsp; NOTES:&nbsp;Selecting&nbsp;All Databases&nbsp;searches Science Citation Index, Biological Abstracts and Medline. The result will indicate the database from which it was retrieved.Citations can be saved directly to EndNote Web from these databases.', NULL, 0, 'Basic', '', 0, 0, NULL),
(162, 'Reference Sources', '<ul>\n	<li>{{dab},{26}, {Corsini encyclopedia of psychology},{01}}</li>\n	<li>{{dab},{30}, {Psychology &amp; mental health},{01}}</li>\n	<li><a href="http://search.credoreference.com.ezproxy.drew.edu/" target="_blank">Credo </a>- online encyclopedia covering ALL subject areas.</li>\n	<li>{{dab},{210}, {Encyclopedia of Evolution},{01}}</li>\n	<li><span style="font-family: Arial, sans-serif; font-size: 14px; line-height: 20px;">Ref &nbsp;</span><span style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.63636302947998px; line-height: normal; text-align: -webkit-center;">HX626 .T73 1999</span><span style="font-family: Arial, sans-serif; font-size: 14px; line-height: 20px;">&nbsp; - Utopias and Utopians: An Historical Dictionary (1999) and <a href="http://site.ebrary.com.ezproxy.drew.edu/lib/drew/detail.action?docID=10005582" target="_blank">online</a></span></li>\n	<li><span style="font-family: Arial, sans-serif; font-size: 14px; line-height: 20px;">Ref </span><span style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.63636302947998px; line-height: normal; text-align: -webkit-center;">HX626 .H65 1998&nbsp;</span><span style="font-family: Arial, sans-serif; font-size: 14px; line-height: 20px;">&nbsp;- The ABC-CLIO World History Companion to Utopian Movements (1998)</span></li>\n	<li><span style="font-family: Arial, sans-serif; font-size: 14px; line-height: 20px;">Ref </span><span style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13.63636302947998px; line-height: normal; text-align: -webkit-center;">HX806 .M626 2004</span><span style="font-family: Arial, sans-serif; font-size: 14px; line-height: 20px;">&nbsp; - Historical dictionary of utopianism (2004)</span></li>\n	<li><span style="font-family: Arial, sans-serif; font-size: 14px; line-height: 20px;">Ref Counter 616.89075 D536d 2013&nbsp;- &nbsp;&nbsp;Diagnostic and Statistical Manual of Mental Disorders: DSM- V-TR (2013)</span></li>\n	<li><span style="font-family: Arial, sans-serif; font-size: 14px; line-height: 20px; color: rgb(0, 0, 0);">Ref</span><span style="font-family: Arial, sans-serif; font-size: 14px; line-height: 20px;">&nbsp;</span><span style="font-family: Arial, sans-serif; font-size: 14px; line-height: 20px; color: rgb(0, 0, 0);">&nbsp;HQ1115 .E54 2009 -&nbsp;</span><span style="font-family: Arial, sans-serif; font-size: 14px; line-height: 20px; color: rgb(0, 0, 0);">Encyclopedia of gender and society</span></li>\n	<li><span style="font-family: Arial, sans-serif; font-size: 14px; line-height: 20px; color: rgb(0, 0, 0);">Ref</span><span style="font-family: Arial, sans-serif; font-size: 14px; line-height: 20px;">&nbsp;</span><span style="font-family: Arial, sans-serif; font-size: 14px; line-height: 20px; color: rgb(0, 0, 0);">BV4580 .A267 2003 &nbsp;</span><span style="font-family: Arial, sans-serif; font-size: 14px; line-height: 20px; color: rgb(0, 0, 0);">Aging, spirituality, and religion : a handbook</span></li>\n	<li><span style="font-family: Arial, sans-serif; font-size: 14px; line-height: 20px; color: rgb(0, 0, 0);">Ref&nbsp;</span><span style="font-family: Arial, sans-serif; font-size: 14px; line-height: 20px; color: rgb(0, 0, 0);">GN69.8 .H34 2009&nbsp;</span><span style="font-family: Arial, sans-serif; font-size: 14px; line-height: 20px; color: rgb(0, 0, 0);">Handbook of forensic anthropology and archaeology</span></li>\n</ul>\n', NULL, 0, 'Basic', '', 0, 0, NULL),
(163, 'Search Tips', '<ul style="margin: 10px 0px 0px; font-family: Arial, sans-serif; font-size: 14px; line-height: 20px;">\n	<li>Use&nbsp;<strong>quotation marks</strong>&nbsp;around phrases: &quot;mental illness&quot;&nbsp;</li>\n	<li>Use the&nbsp;<strong>*</strong>&nbsp;symbol to&nbsp;<strong>truncate</strong>&nbsp;terms: utopia* = utopia; utopias; utopian; utopianism</li>\n	<li>Use&nbsp;<strong>OR</strong>&nbsp;for synonyms: transpersonal OR spiritual</li>\n	<li>Use&nbsp;<strong>AND</strong>&nbsp;to include more than one term in your search: utopia* AND society</li>\n</ul>\n', NULL, 0, 'Basic', '', 0, 0, NULL),
(165, 'Get Full Text', '<p style="margin: 10px 0px 0px; padding: 0px; font-family: Arial, sans-serif; font-size: 14px; line-height: 20px;">Click directly to the full text if available (as pdf or html) or click on&nbsp;<img class="confluence-embedded-image confluence-external-resource" data-image-="" src="http://depts.drew.edu/lib/AL_button.gif" style="margin: 0px; padding: 0px; border: 0px; cursor: pointer; max-width: 100%;" />&nbsp;to find the full text in another database.The&nbsp;<a href="https://uknow.drew.edu/confluence/display/Library/Drew+ArticleLinker" rel="nofollow" style="color: rgb(50, 108, 166); text-decoration: none;">Article Linker guide</a>provides additional instructions for finding full text, either online or in print/microfilm format within the library.</p>\n\n<p style="margin: 10px 0px 0px; padding: 0px; font-family: Arial, sans-serif; font-size: 14px; line-height: 20px;">If the&nbsp;&nbsp;<img class="confluence-embedded-image confluence-external-resource confluence-content-image-border" data-image-="" src="https://uknow.drew.edu/confluence/download/attachments/32123158/AL_button.gif?version=1&amp;modificationDate=1377096646482&amp;api=v2" style="margin: 0px; padding: 0px; border: 1px solid black; cursor: pointer; max-width: 100%;" />&nbsp;button doesn&#39;t provide access to the full text, you can request the article via<a href="https://illiad.drew.edu/illiad/Logon.html" target="_blank"> InterLibraryLoan</a>.<br />\n<img alt="" src="http://walter.drew.edu/splus/assets/users/_epatters/request_an_ILL.png" style="width: 364px; height: 124px;" /><br />\n<br />\nThe first time you request an article or book via InterLibraryLoan, you &nbsp;will &nbsp;be asked to fill out a short form. <b>It may take several days to receive an article via InterLibraryLoan; &nbsp;books can take longer.</b></p>\n', NULL, 0, 'Basic', '', 0, 0, NULL),
(166, 'Find Newspapers/Magazine Articles', '<p style="margin: 10px 0px 0px; padding: 0px; font-family: Arial, sans-serif; font-size: 14px; line-height: 20px;">All are accessible from&nbsp;<a class="external-link" href="http://www.drew.edu/library/research/databases-by-title-a-z" rel="nofollow" style="color: rgb(50, 108, 166); text-decoration: none;" target="_blank">A-Z Databases by Title</a></p>\n\n<ul style="margin: 10px 0px 0px; font-family: Arial, sans-serif; font-size: 14px; line-height: 20px;">\n	<li><em>Academic Search Premiere</em>&nbsp;- after searching, limit your results to magazines or newspapers in the&nbsp;<strong>Source Types</strong>&nbsp;box:<img class="confluence-embedded-image" data-image-="" src="https://uknow.drew.edu/confluence/download/attachments/22315350/ebsco_souirce.jpg?version=1&amp;modificationDate=1379074890477&amp;api=v2" style="margin: 0px; padding: 0px; border: 0px; cursor: pointer; max-width: 100%;" /></li>\n	<li><em>ProQuest Research Library</em>&nbsp;- after searching, limit your results to magazines or newspapers in the&nbsp;<strong>Source Type</strong>&nbsp;box:<img class="confluence-embedded-image" data-image-="" src="https://uknow.drew.edu/confluence/download/attachments/22315350/proquest_sources.jpg?version=1&amp;modificationDate=1379074947745&amp;api=v2" style="margin: 0px; padding: 0px; border: 0px; cursor: pointer; max-width: 100%;" /></li>\n	<li>Try <a href="http://www.drew.edu/library/research" target="_blank">Summon</a> for a really wide search.</li>\n</ul>\n', NULL, 0, 'Basic', '', 0, 0, NULL),
(167, 'Find Scholarly Articles', 'Subject specific databases. These databases contain mostly scholarly or academic articles. Find this databases on the <a href="http://www.drew.edu/library/research/databases-by-title-a-z" target="_blank">&nbsp;A-Z Databases by Title</a> page:\n<ul style="margin: 0px; list-style-type: disc;">\n	<li>PsycINFO</li>\n	<li>SocINDEX</li>\n	<li>GenderWatch</li>\n	<li>Philosophers Index</li>\n	<li>Biomedical Reference Center</li>\n	<li>ScienceDirect (<strong style="padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Verdana, Arial, sans-serif; font-size: 13px; line-height: 19.5px;">&nbsp;</strong><span style="color: rgb(0, 0, 0); font-family: Verdana, Arial, sans-serif; font-size: 13px; line-height: 19.5px;">To access full text articles in ScienceDirect,</span><br style="padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Verdana, Arial, sans-serif; font-size: 13px; line-height: 19.5px;" />\n	<span style="color: rgb(0, 0, 0); font-family: Verdana, Arial, sans-serif; font-size: 13px; line-height: 19.5px;">you must register and login.&nbsp;</span><a href="http://uknow.drew.edu/confluence/x/uQCeAg" style="padding: 0px; margin: 0px; color: rgb(85, 26, 139); text-decoration: none; border: 0px; font-size: 13px; font-family: Verdana, Arial, sans-serif; vertical-align: baseline; line-height: 19.5px;" target="_blank">Click here for instructions.</a>)</li>\n</ul>\n', NULL, 0, 'Basic', '', 0, 0, NULL),
(168, 'Table of Contents', '', NULL, 0, 'TOC', '{"ticked":"169,166,167,165,162,163,7,3,6"}', 0, 0, NULL),
(169, 'Find Reliable Internet Articles', '<p style="margin: 10px 0px 0px; padding: 0px; font-family: Arial, sans-serif; font-size: 14px; line-height: 20px;">In addition to the sites your professor recommends, you can find reliable internet sites on issues related to this class by&nbsp;including the following with your search terms:</p>\n\n<ul style="margin: 10px 0px 0px; font-family: Arial, sans-serif; font-size: 14px; line-height: 20px;">\n	<li><span style="line-height: 1.4285715;">site:edu &nbsp;= &nbsp;brings back articles from educational institutions</span></li>\n	<li>site:org &nbsp;= &nbsp;brings back sites from organizations, including wikipedia -&nbsp;Click &nbsp;the &quot;talk&quot; button on a Wikipedia &nbsp;page to see how reliable the information is:&nbsp;<img alt="" src="http://walter.drew.edu/splus/assets/users/_epatters/wikipedia_talk_button.png" style="width: 332px; height: 127px;" /></li>\n	<li>Search <a href="http://scholar.google.com/" target="_blank">Google Scholar</a> for academic sources</li>\n</ul>\n<br />\n<strong>Try these websites:</strong>\n\n<ul style="margin: 10px 0px 0px; font-family: Arial, sans-serif; font-size: 14px; line-height: 20px;">\n	<li><a class="external-link" href="http://hdr.undp.org/en/reports/" rel="nofollow" style="color: rgb(50, 108, 166); text-decoration: none;">United Nations Human Development Reports</a>&nbsp;are available from the United Nations Development Program. Provides the UN&#39;s blueprint for a multicultural society.</li>\n	<li><a class="external-link" href="http://www.human-nature.com/" rel="nofollow" style="color: rgb(50, 108, 166); text-decoration: none;" target="_blank">Human Nature Review</a>&nbsp;- &quot;This online publication features articles, links, and book reviews from a variety of disciplines and viewpoints with the goal of bringing &quot;into communication the variety of approaches to the understanding of human nature which have a regrettable tendency to be less in touch with one another than they might.&quot; &quot; The articles in this online journal have an evolutionary focus.</li>\n	<li>{{dab},{481}, {Bioethics Research Library at Georgetown University},{01}}</li>\n	<li>{{dab},{1002}, {Astrobiology Magazine},{01}}</li>\n	<li><a class="external-link" href="http://users.erols.com/jonwill/utopialist.htm" rel="nofollow" style="color: rgb(50, 108, 166); text-decoration: none;" target="_blank">Utopia on the Internet</a>&nbsp;- Links to organizations, communities, literature and more.</li>\n	<li><a class="external-link" href="http://dmoz.org/" rel="nofollow" style="color: rgb(50, 108, 166); text-decoration: none; line-height: 1.4285715;" target="_blank">DMOZ: Open Directory Project</a>&nbsp;- use search terms such as culture, human behavior, etc. to create lists that link to organizations and sites focused on specific lifestyles and cultures.</li>\n	<li>{{dab},{685}, {Internet Public Library},{01}}</li>\n	<li>{{dab},{815}, {Association for Psychological Science &ndash; Psychology Links},{01}}</li>\n	<li>{{dab},{816}, {Encyclopedia of Psychology &ndash; Psychology Websites},{01}}</li>\n	<li>{{dab},{810}, {Mental Health &amp; Psychology Resources Online},{01}}</li>\n</ul>\n', NULL, 0, 'Basic', '', 0, 0, NULL),
(170, 'Databases', '<strong>General</strong><br />\n{{dab},{2}, {SUMMON},{11}}<br />\n<br />\n<strong>Subject searching</strong><br />\n{{dab},{4}, {Academic Search Premier},{11}}<br />\n{{dab},{206}, {Proquest Science Journals},{11}}<br />\n<br />\n<strong>Citation searching</strong><br />\n{{dab},{80}, {Web of Science and Knowledge (ISI Citation Indexes)},{11}}<br />\n{{dab},{18}, {Google Scholar},{11}}', NULL, 0, 'Basic', '', 0, 0, NULL);
