(222, 'Helpful sources', '<strong>Electronic resources</strong><br />\n{{dab},{1106}, {Science in Nineteenth-Century Literature},{11}}<br />\n<br />\n{{dab},{1105}, {Science and Modern Literature},{11}}<br />\n<br />\n<strong>Reference Books</strong><br />\n{{dab},{1104}, {Science Fact and Science Fiction: An Encyclopedia},{11}}<br />\n<br />\n<strong>Link to Class Presentation<br />\n<a href="https://docs.google.com/presentation/d/1KmwrLDJpFSvkrpT0ClZrIpJaqN1KfXM2dhtoCztkLAQ/edit?usp=sharing">Google Presentation Slides</a></strong><br />\n&nbsp;', NULL, 0, 'Basic', '', 0, 0, NULL),
(223, 'Summaries and Definitions', '<strong>Check these for background and quick definitions, to answer factual questions or to get a quick overview of a topic:</strong><br />\n<br />\n<strong>General:</strong><br />\n<br />\n{{dab},{51}, {Credo Reference},{01}}<br />\n<br />\n{{dab},{56}, {Oxford English Dictionary (OED)},{01}}<br />\n<br />\n<b>Literary Theory:</b><br />\n<br />\n{{dab},{1058}, {Edinburgh encyclopedia of modern criticism and theory, 2002},{01}}<br />\n{{dab},{1085}, {Encyclopedia of Contemporary Literary Theory, 1993.},{00}}<br />\n<br />\n{{dab},{1082}, {Encyclopedia of Literary and Cultural Theory, 2011.},{01}}<br />\n<br />\n{{dab},{1083}, {Johns Hopkins Guide to Literary Theory &amp; Criticism, 2005.},{01}}<br />\n{{dab},{1084}, {Key Concepts in Postcolonial LIterature},{01}}<br />\n<br />\n<strong>Literature and Authors:</strong><br />\n<br />\n{{dab},{42}, {Literature Criticism Online},{01}}<br />\n<br />\n{{dab},{3}, {Literature Resource Center},{01}}<br />\n<br />\n{{dab},{1086}, {New Literary History of America, 2009},{01}}<br />\n<br />\n{{dab},{121}, {Oxford Encyclopedia of American Literature},{01}}<br />\n<br />\n{{dab},{122}, {Oxford Encyclopedia of British Literature},{01}}<br />\n<br />\n<strong>Historical Background:</strong><br />\n<br />\n{{dab},{1195}, {Dictionary of American History},{01}}<br />\n<br />\n{{dab},{1193}, {Encyclopedia of the Romantic Era, 1760-1850. 2004},{01}}<br />\n<br />\n{{dab},{1168}, {Encyclopedia of the Victorian Era, 2004.},{01}}<br />\n<br />\n{{dab},{1169}, {Reader&#39;s Guide to British History, 2003.},{01}}', NULL, 0, 'Basic', '', 0, 0, NULL);
