INSERT INTO `pluslet` (`pluslet_id`, `title`, `body`, `local_file`, `clone`, `type`, `extra`, `hide_titlebar`, `collapse_body`, `titlebar_styling`) VALUES
(234, 'Find scholarly articles', '<span style="font-size: 13px;">From&nbsp;</span><a href="http://www.drew.edu/home/" style="font-size: 13px;" target="_blank">Treehouse</a><span style="font-size: 13px;">, click on Research Resources:<br />\n<img alt="" src="http://walter.drew.edu/splus/assets/users/_epatters/treehouse_research_resources.png" style="width: 363px; height: 127px;" /><br />\nClick on List of Resources by Title (A-Z)<br />\n<img alt="" src="http://walter.drew.edu/splus/assets/users/_epatters/a-z_list.png" style="width: 496px; height: 154px;" /></span><br />\nAcademic Search Premier is a good place to start your search:<br />\n<img alt="" src="http://walter.drew.edu/splus/assets/users/_epatters/academic_search_premier.png" style="width: 599px; height: 469px;" /><br />\n<br />\nUse separate search boxes for separate topics.<br />\nUse quotations marks &quot;&quot; for phrases: &quot;health inequality&quot;<br />\nConnect synonyms with OR: &quot;united states&quot; OR america*<br />\nUse the asterix * to truncate words:&nbsp;\n<ul>\n	<li>america* = america/american</li>\n</ul>\n<img alt="" src="http://walter.drew.edu/splus/assets/users/_epatters/academic_search_premier_health_inequality.png" style="width: 574px; height: 175px;" /><br />\n<br />\n<br />\n<br />\nAfter searching, make &nbsp;sure to limit your search to Scholarly (Peer Reviewed) Journals<br />\n<img alt="" src="http://walter.drew.edu/splus/assets/users/_epatters/scholarly_articles.png" style="width: 257px; height: 284px;" /><br />\n<br />\nIf there is no link to the full text:&nbsp;<img alt="" src="http://walter.drew.edu/splus/assets/users/_epatters/html_pdf.png" style="width: 379px; height: 36px;" />&nbsp;click on the article linker icon:&nbsp;<img alt="" src="http://walter.drew.edu/splus/assets/users/_epatters/article_linker.png" style="width: 116px; height: 35px;" />.<br />\n<br />\nIf the full tex is available from another database, you&#39;ll get a screen with hyperlinks to the article:<br />\n<img alt="" src="http://walter.drew.edu/splus/assets/users/_epatters/article_linker_results.png" style="width: 674px; height: 172px;" /><br />\n<br />\nIf there are no hyperlinks to an article, click on &quot;Request and InterLibrarLoan&quot;:<br />\n<img alt="" src="http://walter.drew.edu/splus/assets/users/_epatters/request_an_ILL.png" style="width: 364px; height: 124px;" /><br />\n<br />\n&nbsp;', NULL, 0, 'Basic', '', 0, 0, NULL),
(235, 'Search the catalog', 'From <a href="http://www.drew.edu/home/" target="_blank">Treehouse</a>, click on Library Catalog:<br />\n<img alt="" src="http://walter.drew.edu/splus/assets/users/_epatters/library_catalog.png" style="width: 361px; height: 120px;" /><br />\n<br />\nDo a simple search from default search page:<br />\n<img alt="" src="http://walter.drew.edu/splus/assets/users/_epatters/income_inequality.png" style="width: 419px; height: 97px;" /><br />\nOr use the Exact option to browse for books related to your topic:<br />\n<img alt="" src="http://walter.drew.edu/splus/assets/users/_epatters/exact_search.png" style="width: 345px; height: 370px;" /><br />\n<br />\nEXAMPLE:<br />\n<img alt="" src="http://walter.drew.edu/splus/assets/users/_epatters/subjects_catalog.png" style="width: 647px; height: 116px;" /><br />\n<br />\nBrowse subjects:<br />\n<img alt="" src="http://walter.drew.edu/splus/assets/users/_epatters/income_distribution.png" style="width: 479px; height: 116px;" /><br />\n<br />\n&nbsp;', NULL, 0, 'Basic', '', 0, 0, NULL),
(236, 'Find statistics', '<ul>\n	<li><a href="https://www.census.gov/hhes/www/income/data/historical/inequality/" target="_blank">Historical Income Tables: Income Inequality</a> (from the Census)</li>\n	<li><a href="http://www.census.gov/newsroom/releases/archives/income_wealth/cb14-156.html" target="_blank">Gap Between Higher-and Lower-Wealth Households Widens, Census Bureau Reports</a></li>\n	<li><a href="http://povertydata.worldbank.org/poverty/home/" target="_blank">Poverty and Equity</a> Data, from the Worldbank</li>\n	<li><a href="http://data.worldbank.org/topic/poverty" target="_blank">Poverty indicators</a>, from the Worldbank</li>\n	<li><a href="http://data.un.org/" target="_blank">UNdata</a></li>\n	<li><a href="http://www.who.int/research/en/" target="_blank">WHO data and statistics</a> (World Health Organization from the United Nations)</li>\n</ul>\n', NULL, 0, 'Basic', '', 0, 0, NULL),
(237, 'Databases and Indexes', '<strong>Sources such as articles, essays and books tend to answer deeper, less superficial questions.</strong> &nbsp;Retrieve them using the following tools:<br />\n<br />\n<strong>Journal Articles and Essays:</strong><br />\n<br />\nTo find magazine or journal articles, you should use an index, which may or may not include the actual articles. &nbsp;On the <a href="http://www.drew.edu/library/research">Research Resources</a> page of the Library&#39;s web site, under the heading <a href="http://www.drew.edu/library/research/subject-databases">List of Resources by Subject Area</a>, you&#39;ll find descriptions of specific indexes, arranged by topic.<br />\n<br />\n<strong>Core:</strong><br />\n{{dab},{40}, {MLA Bibliography},{01}}<br />\n<br />\n<strong>Supplemental:</strong><br />\n<br />\n{{dab},{66}, {America History and Life},{01}}<br />\n<br />\n{{dab},{1087}, {American Literary Scholarship, },{01}}<br />\n<br />\n{{dab},{18}, {Google Scholar},{01}}<br />\n<br />\n{{dab},{74}, {Historical Abstracts},{01}}<br />\n<br />\n{{dab},{90}, {International Medieval Bibliography Online},{01}}<br />\n<br />\n{{dab},{2}, {SUMMON},{01}}<br />\n<br />\n{{dab},{54}, {Women&#39;s Studies International},{01}}<br />\n<br />\n{{dab},{1088}, {Year&#39;s Work in English Studies (YWES)},{01}}<br />\n<br />\n<strong>Books:</strong><br />\n<br />\nTo find books owned by Drew, check Drew Library Catalog. &nbsp;If you&#39;re looking for&nbsp;<strong>criticism of a specific author</strong>, select &quot;ubject,&quot; and enter your author&#39;s name, last name first:<br />\n<br />\n<img alt="" src="http://walter.drew.edu/splus/assets/users/_jcaldwel/faulkner.jpg" style="width: 291px; height: 55px; border-width: 1px; border-style: solid;" /><br />\n<br />\n{{dab},{94}, {Worldcat},{01}}<br />\n&nbsp;', NULL, 0, 'Basic', '', 0, 0, NULL),
(238, 'Table of Contents', '', NULL, 0, 'TOC', '{"ticked":"360,235,234,236,303,302,7,3"}', 0, 0, NULL),
(239, 'Citation Formating', 'The standard guide is<br />\n{{dab},{916}, {MLA Handbook for Writers of Research Papers},{00}}<br />\nA useful guide to MLA style is&nbsp;{{dab},{316}, {MLA Formatting and Style Guide &ndash; The OWL at Purdue},{00}}<br />\n<br />\n{{dab},{1045}, {EndnoteWeb},{01}}<br />\n&nbsp;', NULL, 0, 'Basic', '', 0, 0, NULL),
(241, '10/6:  Asst 7 - Finding a Journal Citat', 'To find a specific magazine journal article, first locate the publication by going to &nbsp;<a href="http://www.drew.edu/library/research" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;">Research Resources</a>&nbsp;and click on the link labeled <a href="http://www.drew.edu/library/research/journals" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no''); return false;">Journal List Search</a>. &nbsp;Then enter the name of the publication:<br />\n<img alt="" src="http://walter.drew.edu/splus/assets/users/_jcaldwel/journal.png" style="border-width: 1px; border-style: solid; width: 381px; height: 67px;" /><br />\nThen select one of the options:<br />\n<img alt="" src="http://walter.drew.edu/splus/assets/users/_jcaldwel/plos_one.jpg" style="width: 429px; height: 133px; border-width: 1px; border-style: solid;" /><br />\nEither click to search, or bore down to the specific volume, issue or date that you need:<br />\n<img alt="" src="http://walter.drew.edu/splus/assets/users/_jcaldwel/issue.jpg" style="width: 792px; height: 382px; border-width: 1px; border-style: solid;" /><br />\n<br />\n&nbsp;', NULL, 0, 'Basic', '', 0, 0, NULL),
(242, '9/29:  Asst 6 - Using Google', 'Use <a href="http://google.com">Google</a> to search for your topic. &nbsp;If there are phrases in your topic, enclose them in quotation marks:\n<ul>\n	<li><em>&quot;human taphonomy&quot;</em></li>\n	<li><em>&quot;human body decomposition&quot;</em></li>\n</ul>\n', NULL, 0, 'Basic', '', 0, 0, NULL),
(243, '10/17:  Asst 8 - Comparing Search Tools', 'When searching, remember phrases of two or more words should be enclosed in quotation marks: &nbsp;<em>&quot;trophy skulls&quot;</em><br />\n<br />\n{{dab},{47}, {Google},{00}}<br />\n<br />\n{{dab},{18}, {Google Scholar},{01}}<br />\n<br />\n{{dab},{4}, {Academic Search Premier},{01}}<br />\n<br />\n{{dab},{2}, {SUMMON},{01}}', NULL, 0, 'Basic', '', 0, 0, NULL),
(244, '11/3:  Asst 11', 'Look for clues in the secondary source, which might include author, affiliation, data, topic, where the primary source was published. &nbsp;<br />\n<br />\nThen go to<a href="http://ezproxy.drew.edu/login?url=http://search.ebscohost.com/login.aspx?authtype=ip,uid&amp;profile=ehost&amp;defaultdb=aph" onclick="window.open(this.href, '''', ''resizable=yes,status=no,location=yes,toolbar=no,menubar=yes,fullscreen=no,scrollbars=yes,dependent=no''); return false;"> </a><a href="http://ezproxy.drew.edu/login?url=http://search.ebscohost.com/login.aspx?authtype=ip,uid&amp;profile=ehost&amp;defaultdb=aph">Academic Search Premier</a>&nbsp;and enter the clues.', NULL, 0, 'Basic', '', 0, 0, NULL),
(245, '10/27:  Asst 10', 'See instructions for 10/6', NULL, 0, 'Basic', '', 0, 0, NULL),
(246, '11/24:  Asst 14 - Finding Articles', 'To find a scholarly article, go to <a href="http://ezproxy.drew.edu/login?url=http://search.ebscohost.com/login.aspx?authtype=ip,uid&amp;profile=ehost&amp;defaultdb=aph" onclick="window.open(this.href, '''', ''resizable=yes,status=no,location=yes,toolbar=no,menubar=yes,fullscreen=no,scrollbars=yes,dependent=no''); return false;">Academic Search Premier</a> or <a href="http://ezproxy.drew.edu/login?url=http://search.proquest.com/pqrl?accountid=10558" onclick="window.open(this.href, '''', ''resizable=yes,status=no,location=yes,toolbar=no,menubar=yes,fullscreen=no,scrollbars=yes,dependent=no''); return false;">Proquest Research Library</a>&nbsp;(two large general databases), and restrict your search to &quot;scholarly&quot; or &quot;peer reviewed&quot;:<br />\n<br />\n<img alt="" src="http://walter.drew.edu/splus/assets/users/_jcaldwel/PRLASP.jpg" style="width: 800px; height: 279px;" /><br />\nEnter your terms, and then check the results - if one or more look particularly useful, check the subjects assigned to them, and search those terms as well.<br />\n<br />\nIf the pdf or html of an article you want is not listed in the database, click the &quot;Search for Article&quot; icon to see if Drew owns or has access to it:<br />\n<img alt="" src="http://walter.drew.edu/splus/assets/users/_jcaldwel/DNA.png" style="width: 770px; height: 147px; border-width: 1px; border-style: solid;" /><br />\nThis will lead to a listing of where it is:<br />\n<img alt="" src="http://walter.drew.edu/splus/assets/users/_jcaldwel/DNAarticle.png" style="width: 691px; height: 330px; border-width: 1px; border-style: solid;" /><br />\nIf the article is&nbsp;<strong>not</strong> available, you can request it through Interlibrary Loan:<br />\n<img alt="" src="http://walter.drew.edu/splus/assets/users/_jcaldwel/ILL.png" style="width: 501px; height: 416px; border-width: 1px; border-style: solid;" /><br />\n<br />\n&nbsp;', NULL, 0, 'Basic', '', 0, 0, NULL),
(247, 'Primary Source Materials', '<p style="margin: 0px; font-family: Arial, sans-serif; font-size: 14px; line-height: 20px;">&nbsp;</p>\n\n<p style="margin: 10px 0px 0px; font-family: Arial, sans-serif; font-size: 14px; line-height: 20px;"><strong>Primary Source Materials [print]</strong><br />\n&nbsp; &nbsp;&nbsp;<strong><span data-mce-style="text-decoration: underline;" style="text-decoration: underline;">Latin and Greek</span></strong>&nbsp;<br />\n&nbsp;&nbsp;&nbsp;&nbsp;<em>Patrologia Latina</em>&nbsp;[281.1 M635pa; &amp;&nbsp;<a data-mce-="" href="http://www.documentacatholicaomnia.eu/" style="color: rgb(50, 108, 166) !important; text-decoration: none;">online</a>]<br />\n&nbsp;&nbsp;&nbsp;&nbsp;<em>Patrologia Graeca</em>&nbsp;[281.1 M635p; &amp;&nbsp;<a data-mce-="" href="http://www.ellopos.net/elpenor" style="color: rgb(50, 108, 166) !important; text-decoration: none;">online</a>]<br />\n&nbsp;&nbsp;&nbsp;&nbsp;<em>Corpus Christianorum: Series Latina</em>&nbsp;[281.1 C822c ser. lat.; selective]<br />\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong><em>nota bene</em></strong>:&nbsp;<em>Clavis Patrum Latinorum</em>&nbsp;[and other Clavis volumes] [Ref. 281.1 D328c]<br />\n<em>&nbsp;&nbsp;&nbsp;</em>&nbsp;<em>Corpus Christianorum: Series Graeca</em>&nbsp;[281.1 C822c ser. gr.; selective]<br />\n<em>&nbsp;&nbsp;&nbsp;</em>&nbsp;<em>Corpus Christianorum: Continuatio Mediaevalis</em>&nbsp;[281.1 C822c ser. med.]<br />\n&nbsp;&nbsp;&nbsp;&nbsp;<em>Corpus Scriptorum Ecclesiasticorum Latinorum</em>&nbsp;[check individual authors]</p>\n\n<p style="margin: 10px 0px 0px; font-family: Arial, sans-serif; font-size: 14px; line-height: 20px;">&nbsp;&nbsp;&nbsp;&nbsp;<strong>Translations</strong><br />\n&nbsp;&nbsp;&nbsp;&nbsp;<em>Loeb Classical Library</em>&nbsp;[check individual authors]<br />\n&nbsp;&nbsp;&nbsp;&nbsp;<em>Classics of Western Spirituality</em>&nbsp;[check individual authors]<br />\n&nbsp;&nbsp;&nbsp;&nbsp;<em>Library of Christian Clas