(316, 'Summon search details', '{{dab},{2}, {SUMMON},{11}}<br />\nAccess Summon from the right side of Course Page or from Drew Library&#39;s <a href="http://www.drew.edu/library/research" target="_blank">Research Resources page</a>.<br />\n<br />\n<img alt="" src="http://walter.drew.edu/splus/assets/users/_mashmun/summon_1_p1.png" style="width: 555px; height: 371px;" /><br />\n<br />\nSelect Advanced Search option for more selective search parameters.<br />\n<br />\n<img alt="" src="http://walter.drew.edu/splus/assets/users/_mashmun/summon_2_b1.png" style="width: 419px; height: 301px;" /><br />\n<br />\n<br />\nSpecify specfic search terms or dates for more focused results.<br />\n<br />\n<img alt="" src="http://walter.drew.edu/splus/assets/users/_mashmun/summon_3_p1.png" style="width: 539px; height: 687px;" /><br />\n&nbsp;', NULL, 0, 'Basic', '', 0, 0, NULL),
(317, 'Sources', '<strong>Reference sources</strong><br />\n<em>Electronic resources</em><br />\n{{dab},{51}, {Credo Reference},{11}} Search for scientists&#39; biographies, discoveries, or collaborations.<br />\n<br />\n<em>Print resources</em><br />\n<span style="font-size: 13px; line-height: 20.7999992370605px;">The biographical dictionary of scientists. Reference&nbsp;&nbsp;Q141 .B528 2000 vol.1</span><br />\n<br />\nBiographical encyclopedia of scientists / edited by John Daintith. Reference&nbsp;Q141 .B53 2009<br />\n<br />\nThe Cambridge dictionary of scientists / David Millar ... [et al.]. Reference&nbsp;Q141 .C128 2002<br />\n<br />\nIntroduction to the history of science.&nbsp;Reference Q125 .S32 vol.1', NULL, 0, 'Basic', '', 0, 0, NULL),
(318, 'Databases', '<strong>General</strong><br />\n{{dab},{2}, {SUMMON},{11}}<br />\n<br />\n<strong>Subject searching</strong><br />\n{{dab},{4}, {Academic Search Premier},{11}}<br />\n{{dab},{206}, {Proquest Science Journals},{11}}<br />\n<br />\n<strong>Citation searching</strong><br />\n{{dab},{906}, {American Institute of Physics (AIP) Scitation },{11}}<br />\n{{dab},{80}, {Web of Science and Knowledge (ISI Citation Indexes)},{11}}<br />\n{{dab},{18}, {Google Scholar},{11}}', NULL, 0, 'Basic', '', 0, 0, NULL),
(319, 'Get full text', '<strong>Full text available&nbsp;</strong><br />\n<br />\nClick directly to the full text if available in a database, or click on &nbsp; <img src="http://depts.drew.edu/lib/AL_button.gif" />&nbsp; &nbsp;to find the full text in another database.The&nbsp;<a href="https://uknow.drew.edu/confluence/display/Library/Drew+ArticleLinker" target="_blank">Article Linker guide</a>&nbsp;provides additional instructions for finding full text, either online or in print/microfilm format within the library.<br />\n<br />\n<br />\n<strong>Full text not available</strong><br />\n<br />\nDon&#39;t give up! &nbsp;There are other ways to get the information you are seeking.&nbsp;<br />\n<img alt="" src="http://walter.drew.edu/splus/assets/users/_mashmun/find_full_t.png" style="width: 468px; height: 582px;" /><br />\n<br />\n<br />\n<br />\n&nbsp;', NULL, 0, 'Basic', '', 0, 0, NULL);
