(788, 'Additional Resources', '<ul>\n	<li>See also Bruce Lancaster&#39;s Southern Cookbook collection</li>\n	<li>{{dab},{1064}, {Cambridge world history of food},{00}}</li>\n	<li>{{dab},{1271}, {Historical Dictionary of the Old South},{00}}</li>\n	<li>{{dab},{1272}, {The Old South},{00}}</li>\n	<li>{{dab},{1110}, {Oxford Encyclopedia of Food and Drink in America, 2004.},{00}}</li>\n	<li>{{dab},{179}, {Oxford Music Online},{00}}</li>\n	<li>{{dab},{898}, {NAXOS Music Library},{00}}</li>\n</ul>\n', NULL, 0, 'Basic', '', 0, 0, NULL),
(789, 'Databases', '{{dab},{80}, {Web of Science and Knowledge (ISI Citation Indexes)},{11}}\n<ul>\n	<li>{{dab},{1172}, {Citation Searching in Web of Science},{11}}</li>\n</ul>\n{{dab},{29}, {ScienceDirect},{11}}\n\n<ul>\n	<li>{{dab},{1171}, {How to access ScienceDirect articles},{11}}</li>\n</ul>\n{{dab},{1161}, {ProQuest Central},{11}}<br />\n<br />\n<span style="font-size: 13px; line-height: 20.7999992370605px;">{{dab},{18}, {Google Scholar},{11}}</span>\n\n<ul style="font-size: 13px; line-height: 20.7999992370605px;">\n	<li>{{dab},{1173}, {Searching Google Scholar},{11}}</li>\n</ul>\n', NULL, 0, 'Basic', '', 0, 0, NULL);
