(2, 'SUMMON', '', 'Natural language, &quot;Google-like&quot; search across books, some journal articles, and other resources held by Drew University Library.', '', NULL, '2013-05-20 20:15:30', 1),
(3, 'Literature Resource Center', '', 'Author biographies, work overviews, literary criticism and bibliographies.', '', NULL, '2013-05-20 20:17:53', 1),
(4, 'Academic Search Premier', '', 'Interdisciplinary database of newspapers, magazines and journals, with full-text for a majority of the articles. Also includes a searchable file of images.', '', NULL, '2013-05-20 20:28:35', 1),
(5, 'Reference Contact', 'Reference Desk', '<p><b>The Reference Desk</b></p>\r\n\r\n<ul>\r\n	<li>Level C of the Library</li>\r\n	<li>Open 9 am to 5 pm Monday through Friday; 6 pm to 10 pm Sunday through Thursday</li>\r\n	<li>Chat with us via links on the Reference Resources page <a class="external-link" href="http://www.drew.edu/library/research" rel="nofollow">http://www.drew.edu/library/research</a><br />\r\n	or Ask a Librarian&nbsp; <a class="external-link" href="http://www.drew.edu/library/ask-a-librarian" rel="nofollow">http://www.drew.edu/library/ask-a-librarian</a></li>\r\n	<li>Email reference@drew.edu</li>\r\n	<li>Call 973-408-3588 or text 973-440-8LIB</li>\r\n</ul>\r\n', '', NULL, '2013-05-30 14:24:23', 0),
(6, 'Improving Adolescent Literacy: Effective Classroom and Intervention Practices', '', 'From the IES What Works Clearinghouse: <a class="external-link" href="http://ies.ed.gov/ncee/wwc/" rel="nofollow">http://ies.ed.gov/ncee/wwc/</a>', '', NULL, '2013-05-30 14:25:46', 0),
(7, 'Handbook of Research on Teaching the English Language Arts', '', '', '', NULL, '2013-05-30 14:31:56', 0),
(8, 'Education Research Complete', '', 'Education Research database searches education-related publications. Full text of many journals as well as some books and conference papers.', '', NULL, '2013-05-30 14:33:35', 0),
(9, 'edSpeak', '', 'Glossary of education terms, phrases, buzzwords and jargon.', '', NULL, '2013-05-30 14:41:37', 0),
(10, 'ERIC', 'ERIC (Education)', 'Selective index of journal articles in Education and reports in Education from 1966 to the present.', '', NULL, '2013-05-30 14:43:00', 0),
(11, 'International Handbook of Educational Evaluation', '', 'A handbook discussing principles, history, and practice of both student and program assessment in education.', '', NULL, '2013-05-30 14:44:35', 0),
(12, 'Teacher Reference Center', '', 'Aimed at educators, librarians and education researchers. Covers 280 peer-reviewed, abstracted and indexed journals. (From the NJ State Libraries)', '', NULL, '2013-05-30 14:45:32', 1),
(13, 'Proquest Education Journals', '', 'Full text for about 600 education publications, plus indexing for about 300 more.', '', NULL, '2013-05-30 14:48:16', 0),
(14, 'PsycINFO', 'Psychological Abstracts', 'Indexes and abstracts the international journal, and book literature in psychology and related disciplines since 1887. Lots of full text is available. Click on the &quot;find article&quot; icon if the pdf or html link to the article is not visible.', '', NULL, '2013-05-30 14:49:54', 0),
