(17, 'National Council of Teachers of English', '', '', '', NULL, '2013-05-30 15:13:14', 0),
(18, 'Google Scholar', '', 'This free, interdisciplinary resource indexes the available text of <strong>scholarly</strong> journal sites and scholarly website collections on the Internet, and allows citation searching. No subject searching.<br />\r\nMake sure Scholar Preferences are set to include Library Links from Drew to check subscription content availability; some collections, however, are not linked.', '', NULL, '2013-05-30 15:16:59', 0),
