(260, 'Annotated Constitution', '', 'Excellent resource for understanding the U.S. Constitution.', '', NULL, '2013-08-08 19:51:23', 0),
(261, ' Avalon Project', '', '<span style="font-family: inherit; font-size: inherit; font-style: inherit; font-variant: inherit; line-height: inherit;">&ldquo;The Avalon Project will mount digital documents relevant to the fields of Law, History, Economics, Politics, Diplomacy and Government. We do not intend to mount only static text but rather to add value to the text by linking to supporting documents expressly referred to in the body of the text. &ldquo;</span>', '', NULL, '2013-08-08 19:51:59', 0),
(262, 'BRB Free Public Records', '', '<div class="post-3756 resourceweb type-resourceweb status-publish hentry" style="margin: 0px; padding: 0.1em 0px; border: 0px; font-family: inherit; font-size: inherit; font-style: inherit; font-variant: inherit; line-height: inherit; vertical-align: baseline;"><span style="font-family: inherit; font-size: inherit; font-style: inherit; font-variant: inherit; line-height: inherit;">The &ldquo;most comprehensive directory of free public record sites, reference material, and public record vendors available.&rdquo;</span></div>\r\n', '', NULL, '2013-08-08 19:52:41', 0),
(263, 'Brookings Institution', '', '<div class="post-3982 resourceweb type-resourceweb status-publish hentry" style="margin: 0px; padding: 0.1em 0px; border: 0px; font-family: inherit; font-size: inherit; font-style: inherit; font-variant: inherit; line-height: inherit; vertical-align: baseline;">&quot;<span style="font-family: inherit; font-size: inherit; font-style: inherit; font-variant: inherit; line-height: inherit;">The Brookings Institution is a nonprofit public policy organization based in Washington, DC. Our mission is to conduct high-quality, independent research and, based on that research, to provide innovative, practical recommendations that advance three broad goals: Strengthen American democracy; Foster the economic and social welfare, security and opportunity of all Americans; and Secure a more open, safe, prosperous and cooperative international system.&rdquo;</span></div>\r\n', '', NULL, '2013-08-08 19:53:48', 0),
(264, 'AAAS Scientific Responsibility, Human Rights and Law Program', '', '', '', NULL, '2013-08-08 19:57:43', 0),
(265, 'Brennan Center for Justice', '', '<span style="color: rgb(0, 0, 0); font-family: nimbus-sans, sans-serif; font-size: 16px; line-height: 19px;">&quot;The Brennan Center for Justice at NYU School of Law is a nonpartisan law and policy institute that seeks to improve our systems of democracy and justice. We work to hold our political institutions and laws accountable to the twin American ideals of democracy and equal justice for all. The Center&rsquo;s work ranges from voting rights to campaign finance reform, from racial justice in criminal law to Constitutional protection in the fight against terrorism. A singular institution &mdash; part think tank, part public interest law firm, part advocacy group, part communications hub &mdash; the Brennan Center seeks meaningful, measurable change in the systems by which our nation is governed.&quot;</span>', '', NULL, '2013-08-08 19:58:06', 0),
(266, 'CiteSeer', '', '<div class="post-3717 resourceweb type-resourceweb status-publish hentry">&ldquo;A scientific literature digital library and search engine that focuses primarily on the literature in computer and information science.&rdquo;</div>\r\n', '', NULL, '2013-08-08 20:01:41', 0),
(267, 'Computer Science Bibliographies', '', 'Computer Science Bibliographies maintains a searchable collection of over one million references to journal articles, conference papers, and technical reports, grouped by subject.', '', NULL, '2013-08-08 20:02:18', 0),
(268, 'Computer Science Technical Reports', '', 'Access to a large collection of downloadable reports, including some PhD. theses, fully indexed by all words.', '', NULL, '2013-08-08 20:03:05', 0),
(269, 'Computer Science: A Guide to Web Resources', '', '<div class="post-3725 resourceweb type-resourceweb status-publish hentry">Provides many excellent resources for researchers at all levels: Starting Points, Algorithms, Bibliographies/Technical Reports, Meetings and Conferences, Dictionaries, and much more. Compiled by SUNY Albany Library.</div>\r\n', '', NULL, '2013-08-08 20:17:48', 0),
(270, 'Computing Now', '', '<div class="post-4987 resourceweb type-resourceweb status-publish hentry">A news magazine and academic Internet Portal from the Institute of Electrical and Electronics Engineers (IEEE) Computer Society.</div>\r\n', '', NULL, '2013-08-08 20:18:52', 0),
(271, 'DBLP Computer Science Bibliography', '', '<div class="post-3726 resourceweb type-resourceweb status-publish hentry">&ldquo;Offers bibliographic information on major computer science journals and proceedings,&rdquo; articles, links to home pages of computer scientists. By Michael Ley, at Germany&rsquo;s University of Trier.</div>\r\n', '', NULL, '2013-08-08 20:19:31', 0),
(272, 'E-print Network: Research Communications for Scientists and Engineers', '', '<div class="post-3626 resourceweb type-resourceweb status-publish hentry">Full text of articles in science (especially physics) and technology.</div>\r\n', '', NULL, '2013-08-08 20:20:32', 0),
(273, 'EDUCAUSE: Campus Policies', '', 'Topic primer and links to computer-related policies from types of organizations (colleges and universities, municipalities, corporations, etc.). as well as policy related presentations, podcasts, etc. Searchable or browseable by organization name or policy type (acceptable use, privacy, security, etc). (EDUCAUSE)', '', NULL, '2013-08-08 20:22:12', 0),
(274, 'Google Code University', '', '<div class="post-3716 resourceweb type-resourceweb status-publish hentry">Informative resources and tutorials for programmers and programmers in training. Resources on XML, Python, Linux, , C++, Java, web security, algorithms, and distributed systems.</div>\r\n', '', NULL, '2013-08-08 20:23:04', 0),
(275, 'SciTech Connect', '', '<div class="post-3598 resourceweb type-resourceweb status-publish hentry">Science, technology, and engineering research information from the U.S. Department of Energy.</div>\r\n', '', NULL, '2013-08-08 20:23:34', 0),
(276, 'lanl.arXiv.org e-Print archive mirror', '', '<div class="post-3623 resourceweb type-resourceweb status-publish hentry">Open access to 865,000+ e-prints in Physics, Mathematics, Computer Science, Quantitative Biology and Statistics, owned and funded by Cornell University with partial funding from NSF.</div>\r\n', '', NULL, '2013-08-08 20:25:09', 0),
(277, 'MIT OpenCourseWare: Introduction to Computer Science and Programming', '', '<div class="post-5007 resourceweb type-resourceweb status-publish hentry">Free access to the spring 2011 version of MIT Professor John Guttag&rsquo;s popular &ldquo;Introduction to Computer Science and Programming.&rdquo; For students with little programming experince, it teaches how to &ldquo;write small programs that allow them to accomplish useful goals.&rdquo; Includes lecture &amp; recitation videos, handouts and slides, homework problems, self-assessment tools and collections of supplemental links.</div>\r\n', '', NULL, '2013-08-08 20:27:25', 0),
(278, 'Programmers’ Heaven', '', '<div class="post-3722 resourceweb type-resourceweb status-publish hentry">Gateway to over 17,859 links of value to programmers and developers. In addition to its main sections (languages, applications, platforms, and web development), it provides news, message boards, and mailing lists.</div>\r\n', '', NULL, '2013-08-08 20:28:44', 0),
(279, 'TechXtra: Engineering, Mathematics, and Computing', '', '<div class="post-3721 resourceweb type-resourceweb status-publish hentry">Math and computer science articles, books, websites, industry news, job announcements, technical reports, technical data, full text eprints, research, theses &amp; dissertations, teaching and learning resources.</div>\r\n', '', NULL, '2013-08-08 20:29:36', 0),
(280, 'Writing in College – A Short Guide to College Writing', '', '<div class="post-6452 resourceweb type-resourceweb status-publish hentry">From the University of Chicago.</div>\r\n', '', NULL, '2013-08-08 20:30:34', 0),
(281, 'AMS Digital Mathematics Registry: Journal List', '', '<div class="post-3903 resourceweb type-resourceweb status-publish hentry">List of mathematics journals that are available online worldwide. Items marked with a Globe are freely available/downloadable.</div>\r\n', '', NULL, '2013-08-08 20:41:13', 0),
(282, ' AMS: American Mathematical Society', '', '<div class="post-3913 resourceweb type-resourceweb status-publish hentry">Links to Mathematics journals, bibliographies, listservs, government resources, career information, and other general resources.</div>\r\n', '', NULL, '2013-08-08 20:42:03', 0),
(283, 'Calculus Animations', '', '<div class="post-4289 resourceweb type-resourceweb status-publish hentry">Mathcad 14 animations of calculus learning/teaching demonstrations, in AVI format (use Windows Media, RealAudio, or QuickTime), submitted by math faculty and teachers</div>\r\n', '', NULL, '2013-08-08 20:44:06', 1),
(284, 'College Math Resources', '', '<div class="post-3911 resourceweb type-resourceweb status-publish hentry">Subject-faceted list of unique material, directed toward first- and second-year college students. It covers precalculus, calculus, and some differential equations, supplements to the textbooks.</div>\r\n', '', NULL, '2013-08-08 20:45:09', 0),
(285, 'EqWorld: The World of Mathematical Equations', '', '<div class="post-3898 resourceweb type-resourceweb status-publish hentry">This site contains methods and exact solutions for a multitude of mathematical equations, including algebraic, differential, partial differential, integral, and functional equations</div>\r\n', '', NULL, '2013-08-08 20:46:02', 0),
(286, 'Khan Academy', '', '<div class="post-3602 resourceweb type-resourceweb status-publish hentry">Short video tutorials of math (including algebra and calculus), biology, chemistry, physics.</div>\r\n', '', NULL, '2013-08-08 20:47:55', 0),
(287, 'MAA Distinguished Lecture Series – Past Lectures', '', '<div class="post-3905 resourceweb type-resourceweb status-publish hentry">Online notes, transcripts and audio of lectures from the Mathematical Association of America&rsquo;s Distinguished Lecture Series. Mathematicians discuss current trends in Mathematics in a way that is accessible to the average listener. Transcripts are by MAA staff, but listening to the lectures is a much more accurate way of getting the gist.</div>\r\n', '', NULL, '2013-08-08 20:48:53', 0),
(288, 'MAA Online: Classroom Capsules and Notes', '', '<div class="post-3899 resourceweb type-resourceweb status-publish hentry">Classroom materials for the teaching of undergraduate mathematics, classified by title, author, subject, keywords &amp; relevant course. From the Mathematical Association of America.</div>\r\n', '', NULL, '2013-08-08 20:50:11', 0),
(289, 'MAA: Mathematical Association of America', '', '<div class="post-3912 resourceweb type-resourceweb status-publish hentry">Focuses on mathematics done in undergraduate institutions. Read online journals, news and features, professional development information, and about programs, meetings and employment information.</div>\r\n', '', NULL, '2013-08-08 20:50:42', 0),
(290, 'Martindale’s Calculators On-Line Center / Mathematics Center', '', '<div class="post-3622 resourceweb type-resourceweb status-publish hentry">&ldquo;Calculators, Applets, Spreadsheets, and where Applicable includes: Courses, Manuals, Handbooks, Simulations, Animations, Videos/Movies, etc.&rdquo;</div>\r\n', '', NULL, '2013-08-08 20:51:29', 0),
(291, 'Math Forum @ Drexel University', '', '<div class="post-3772 resourceweb type-resourceweb status-publish hentry">Information and resources for K-16 math teachers from Drexel University, with partial funding from NSF.</div>\r\n', '', NULL, '2013-08-08 20:52:07', 0),
(292, 'Math on the Web', '', '<div class="post-3902 resourceweb type-resourceweb status-publish hentry">A guide and directory to web-available resources in Mathematics, produced by the American Mathematics Society.</div>\r\n', '', NULL, '2013-08-08 20:53:11', 0),
(293, 'Mathematical Atlas: A Gateway to Modern Mathematics', '', '<div class="post-3909 resourceweb type-resourceweb status-publish hentry">Organized according to the Mathematics Subject Classification devised by the AMS. Each division of the classification has introduction, list of subdivisions, bibliography of general works on the subject, selected Web sites with hotlinks, and hotlinks to related sites.</div>\r\n', '', NULL, '2013-08-08 20:53:48', 0),
(294, 'MathVids', '', '<div class="post-3906 resourceweb type-resourceweb status-publish hentry">Free math instructional videos (though extra features require subscription &amp; video ads play before the videos-- download the video to skip the ad), submitted by math faculty and teachers.</div>\r\n', '', NULL, '2013-08-08 21:02:24', 0),
(295, 'MIT OpenCourseware: Mathematics', '', 'Lecture notes, online lectures, problem sets, and practice tests for many levels of undergraduate mathematics. Developed and open-source released by MIT.', '', NULL, '2013-08-08 21:02:56', 0),
(296, 'S.O.S. MATHematics', '', 'Provides refreshers on higher mathematical concepts from algebra to differential equations', '', NULL, '2013-08-08 21:18:26', 0),
(297, 'SIAM: Society for Industrial and Applied Mathematics', '', '<div class="post-3910 resourceweb type-resourceweb status-publish hentry">Features journals online, recently posted articles, activity groups, and information on careers and educational opportunities for mathematics students. Applied mathematics is the focus.</div>\r\n', '', NULL, '2013-08-08 21:19:01', 0),
(298, 'Single Variable Calculus: MIT OpenCourseware', '', '<div class="post-3901 resourceweb type-resourceweb status-publish hentry">Free online version of MIT&rsquo;s calculus course. Look at Video Lectures, lecture notes, problem sets, practice exams.</div>\r\n', '', NULL, '2013-08-08 21:19:42', 0),
(299, 'Trailblazing: Three and a half centuries of Royal Society Publishing', '', '<div class="post-3612 resourceweb type-resourceweb status-publish hentry">A selection of historically significant papers by (mostly) famous scientists. Use the Timeline or Download the PDF and use the&nbsp;&ldquo;find&rdquo; function to search by scientist or keyword.&nbsp;</div>\r\n', '', NULL, '2013-08-08 21:20:11', 0),
(300, 'Betty H. Carter Women Veterans Historical Project', '', '<span style="font-family: Verdana, Verdana, Arial, Helvetica, sans-serif; font-size: 13px; line-height: 19.1875px; text-align: justify;">&quot;The Betty H. Carter Women Veterans Historical Project documents the female experience in the U.S. Armed Forces through letters, papers, photographs, published materials, uniforms, artifacts, and oral histories. It contains more than 500 individual collections which includes 300 oral histories. Housed and maintained in the University Archives in Jackson Library, the materials are a research collection for scholars of military history as well as women&#39;s studies.&quot;</span>', '', NULL, '2013-08-09 13:20:10', 0),
(301, 'Discovering American Women&#039;s History Online', '', '<div class="post-3862 resourceweb type-resourceweb status-publish hentry">This database provides access to digital collections of primary sources (photos, letters, diaries, artifacts, etc.) that document the history of women in the United States.</div>\r\n', '', NULL, '2013-08-09 13:21:14', 0),
(302, 'Feminae: Medieval Women and Gender Index', '', 'Indexes journal articles and essays on women in the Middle Ages.', '', NULL, '2013-08-09 13:21:58', 0),
(303, 'LABORSTA Internet', '', 'International Labor statistics of all kinds, from the International Labour Office.', '', NULL, '2013-08-09 13:22:38', 0),
(304, 'LGBT Religious Archives Network', '', 'Rich gateway to archival resources documenting religious life and history of LGBT persons.', '', NULL, '2013-08-09 13:23:41', 0),
(305, 'WGSSLinks Women and Philosophy Web Sites', '', 'Collection of resources serves as a gateway to feminist work in philosophy and to the professional community of women in philosophy. Includes major link for &ldquo;Feminist Theory.&rdquo; By the Women&rsquo;s Studies Section of ACRL.', '', NULL, '2013-08-09 13:24:37', 0),
(306, 'WomanStats Project', '', '<div class="post-4146 resourceweb type-resourceweb status-publish hentry">Provides information on the status of women worldwide.</div>\r\n', '', NULL, '2013-08-09 13:25:41', 0),
(307, 'Women’s Studies Database', '', 'From the University of Maryland, this links to a variety of topics concerning women and feminism.', '', NULL, '2013-08-09 13:26:19', 0),
(308, 'Women&#039;s Studies Resources', '', 'Culls major news stories on women around the globe.', '', NULL, '2013-08-09 14:05:25', 0),
(309, 'WomensLaws.org', '', 'Provides state-specific legal information and resources for survivors of domestic violence. A project of the National Network to End Domestic Violence (NNEDV).', '', NULL, '2013-08-09 14:07:16', 0),
(310, 'Women Working, 1800-1930', '', '<p>&ldquo;Women Working is a digital exploration of women&rsquo;s impact on the economic life of the United States between 1800 and the Great Depression. Working conditions, workplace regulations, home life, costs of living, commerce, recreation, health and hygiene, and social issues are among the issues documented in this online research collection from Harvard University.&quot;<br />\r\nDigitized pages and images, manuscripts and personal papers, institutional record; cataloged books, magazines, and trade catalogs; and photographs.&rdquo;</p>\r\n', '', NULL, '2013-08-09 14:09:33', 0),
(311, 'Brief Guide to Internet Resources in Theatre and Performance Studies', '', 'By Ken McCoy, Ph.D. Provides comprehensive links to World Wide Web resources, listservs, newsgroups, journals, and indexes.', '', NULL, '2013-08-09 14:11:28', 0),
(312, 'Doollee.com', '', '&ldquo;The free online guide to modern playwrights and theatre plays which have been written, adapted or translated, into English since the production of Look Back in Anger in 1956,&rdquo; by Julian Oddy. Searchable by title, playwright, and number of male and female characters.', '', NULL, '2013-08-09 14:12:06', 0),
(313, 'IBDB (Internet Broadway Database)', '', '&ldquo;The official database for Broadway theatre information. IBDB provides records of productions from the beginnings of New York theatre until today. Details include pertinent people involved as well as interesting facts and production statistics.&rdquo;', '', NULL, '2013-08-09 14:12:41', 0),
(314, 'INTER-PLAY', '', 'Valuable resource for tracking down print copies of a particular play &ldquo;hidden&rdquo; in sources often not searchable in catalogs. &ldquo;Contains . . . citations to plays, many of which are not included in the standard printed play indexes.&rdquo;', '', NULL, '2013-08-09 14:13:37', 0),
(315, 'Jack Woolcott’s Theatre History on the Web', '', 'Well-organized directory of international theater research sites. Links are available for Area Studies (Classical, Elizabethan, 20th Century), Cultural Sites (Theatre Resources, Organizations), and Centers, Museums, and Libraries.', '', NULL, '2013-08-09 14:14:26', 0),
(316, 'MLA Formatting and Style Guide – The OWL at Purdue', '', 'Purdue&rsquo;s Online Writing Lab&rsquo;s guide to the most commonly used citation format in the humanities.', '', NULL, '2013-08-09 14:15:13', 0),
(317, 'Motley Collection of Theatre and Costume Design', '', 'The Motley Collection of Theatre and Costume Design is a valuable source of documentation on the history of theatre, housed in the UIUC Rare Book and Special Collections Library. It is a rare collection of original materials on the theatre comprising over 5000 items from more than 150 productions in England and the United States. These materials include costume and set designs, sketches, notes, photographs, prop lists, storyboards, and swatches of fabric.', '', NULL, '2013-08-09 14:15:45', 0),
(318, 'Playwriting Seminars', '', '&ldquo;The Playwriting Seminars&rdquo; -Significant resource for aspiring playwrights; providing guidance on writing successfully for the regional theater. Well-organized pages oriented toward the practical details which make a play viable in these venues. By Richard Toscan at Va. Commonwealth', '', NULL, '2013-08-09 14:18:11', 0),
(319, 'StageWork', '', 'From The Royal National Theatre of Britain, this web site is a thorough tour of theatrical production. In addition to explorations of the background, sources and themes of recent productions, the site features many performance and interview videos, exploring all aspects of the theatre from marketing and design to directing and rehearsal. Other highlights include a video tour of the National Theatre and a video interview with Sir Ian McKellan with interactive features. This site was commissioned by Culture Online, part of UK&rsquo;s Department for Culture, Media and Sport, and won a British Academy of Film and Television Arts award.', '', NULL, '2013-08-09 14:28:02', 0),
(320, 'Documents of 20th Century Latin American and Latino Art', '', 'Access to primary sources and critical documents tracing the development of twentieth-century art in Latin America and among Latino populations in the United States; from the International Center for the Arts of the Americas, Museum of Fine Arts/Houston.', '', NULL, '2013-08-09 14:29:43', 0),
(321, 'Handbook of Latin American Studies', '', 'Web version of the Handbook of Latin American Studies from the Library of Congress contains the most recent volumes of the scholarly bibliography. Includes books as well as journals, but no direct links to Drew holdings. Earlier volumes of the bibliography are available in the Drew Library.', '', NULL, '2013-08-09 14:31:01', 0),
(322, 'Latin American Business History – Resources and Research', '', 'Oral Histories and listings of Latin American Business History related special collections from Harvard Business School.', '', NULL, '2013-08-09 14:33:16', 0),
(323, 'Latin American Network Information Center', '', 'Top-ranked collection of sites on Central and South America, covering a full range of interests. University of Texas.', '', NULL, '2013-08-09 14:34:07', 0),
(324, 'Lydia Cabrera Papers', '', 'Papers of a leading researcher on Cuban folklore and Afro-Cuban culture and religion.', '', NULL, '2013-08-09 14:35:16', 0),
(325, 'Biblioteca Digital Hispanica', '', 'Digitized collections of the National Library of Spain.', '', NULL, '2013-08-09 14:41:30', 0),
(326, 'Portuguese Culture', '', '<p>&quot;Portuguese Culture is a special Digital Library Project undertaken by the National Library of Portugal with the financial support of the Luso-American Development Foundation... making freely available on the Internet English translations of works by Portuguese authors and other works in English that deal with Portugal and aspects of the Portuguese culture.&quot;</p>\r\n', '', NULL, '2013-08-09 14:42:42', 0),
(327, 'Termium Plus', '', 'The Government of Canada&rsquo;s terminology and linguistic data bank.', '', NULL, '2013-08-09 14:44:17', 0),
(328, 'VoS: Spanish &amp; Portuguese', '', 'Pulls together carefully selected web sites on literature in Spanish. This is a section of one of the most highly regarded directories in the humanities, maintained by UC/Santa Barbara.', '', NULL, '2013-08-09 14:45:12', 0),
(329, 'REESWEB: the Virtual Library for Russian and Eastern European Studies', '', 'Indexes electronic resources on Russia, CIS, Balkans and Eastern Europe; maintained by the Slavic bibliographer at the University of Pittsburgh.', '', NULL, '2013-08-09 14:56:41', 0),
(330, 'Russian Internet Resources', '', 'Beautifully organized index to excellent links pertaining to literature, language, culture, history, government, and politics as well as newspapers, journals, libraries, and museums. From the British Library', '', NULL, '2013-08-09 14:57:35', 0),
(331, 'Zembla', '', 'A site &ldquo;dedicated to the life and works&hellip; of Vladimir Nabokov.&rdquo; By the Arts &amp; Humanities Library at Penn State.', '', NULL, '2013-08-09 14:58:17', 0),
(332, 'Contagion: Historical Views of Diseases and Epidemics', '', 'Digitized historical documents from the collections of Harvard University on diseases and epidemics.', '', NULL, '2013-08-09 14:59:45', 0),
(333, 'Disability.gov', '', 'Disability.gov is the federal government website for comprehensive information on disability programs and services in communities nationwide. The site links to more than 14,000 resources from federal, state and local government agencies; academic institutions; and nonprofit organizations.', '', NULL, '2013-08-09 15:01:21', 0),
(334, 'Gapminder: Unveiling the beauty of statistics for a fact based world view.', '', 'Gapminder World and Gapminder Countries are web-services displaying time series of development statistics for all countries, &quot;converting boring numbers into enjoyable, animated and interactive graphics.&quot;', '', NULL, '2013-08-09 15:04:53', 0),
(335, 'Harvard World Health News', '', 'News and links to other public health related sites.', '', NULL, '2013-08-09 15:11:33', 0),
(336, 'Health Data Tools and Statistics', '', 'A page of links to reliable sources of public health statistics from a partnership of government and public health organizations and health science libraries.', '', NULL, '2013-08-09 15:12:22', 0),
(337, 'Public Health : Health Care Reform', '', 'Health Care Reform legislation, blogs and news, from a librarian at Temple University.', '', NULL, '2013-08-09 15:13:33', 0),
(338, 'Regional Economic Conditions RECON', '', '40,000+ charts, maps and tables of data at the state, MSA (Metropolitan Statistical Area) and county level economic data.', '', NULL, '2013-08-09 15:15:56', 0),
(339, 'States Perform', '', '&ldquo;States Perform provides users with access to interactive, customizable and up-to-date comparative performance measurement data for 50 states in six key areas: fiscal and economic, public safety and justice, energy and environment, transportation, health and human services, and education. &ldquo; From the Council of State Governments.', '', NULL, '2013-08-09 15:20:19', 0),
(340, 'American Institute of Physics — Physics Publications and Resources', '', 'The American Institute of Physics website links to news, publications, and history sites.', '', NULL, '2013-08-09 15:23:17', 0),
(341, 'American Physical Society (APS) Physics', '', 'American Physical Society lists valuable links to web resources concerning Physics, such as Journals, Education, Forums, Career Development and a great deal more.', '', NULL, '2013-08-09 15:23:55', 0),
(342, 'Google Sky', '', 'Locate positions of constellations and planets, view universe at different wavelengths, see historical drawings of constellations using this website that is the result of a collaboration among Google, NASA, and several other organizations.', '', NULL, '2013-08-09 15:27:43', 0),
(343, 'Martindale’s The Reference Desk', '', 'Links to online resources on a wide variety of topics-- choose the science from the list.', '', NULL, '2013-08-09 15:32:28', 0),
(344, 'NIST Handbook of Basic Atomic Spectroscopic Data', '', 'From the introduction: &ldquo;a selection of the most important and frequently used atomic spectroscopic data in an easily accessible format.&rdquo;', '', NULL, '2013-08-09 15:33:21', 0),
(345, 'PhysicsWorld.Com', '', '<span id="docs-internal-guid-3afd3adc-48ed-81bc-508c-a42f0dc9986e"><span style="font-size: 13px; font-family: Arial; vertical-align: baseline; white-space: pre-wrap;">Online resource that supports print&nbsp;magazine,</span><span style="font-size: 13px; font-family: Arial; font-style: italic; vertical-align: baseline; white-space: pre-wrap;"> Physics World</span><span style="font-size: 13px; font-family: Arial; vertical-align: baseline; white-space: pre-wrap;">. Includes high quality general information articles about current topics relating to physics and physical-science in general. &nbsp;Website highlight includes &ldquo;Multimedia&rdquo; tab with links to videos, webinars and podcasts offering a wide range of information including explanations of various topics of interest and/or interviews with prominent persons who are making (or who have made) an impact in the field of physics. </span></span>Offers news, a calendar, jobs and many &ldquo;Web Links&rdquo; including the areas of education, companies, reference, institutions and humor. From the Institute of Physics.', '', NULL, '2013-08-09 15:34:10', 0),
(346, 'PhysLink.com', '', 'Provides physics web resources in an organized, easy to use format.', '', NULL, '2013-08-09 15:34:43', 0),
(347, 'Science.gov ', '', 'A gateway to authoritative selected science information provided by U.S. Government agencies, including research and development results.', '', NULL, '2013-08-09 15:36:30', 0),
(348, 'Writing Guidelines for Engineering and Science Students', '', 'Learn to design posters and power point presentations as well as how to write proposals, lab reports, dissertations, journal articles.', '', NULL, '2013-08-09 15:38:48', 0),
(349, 'EpistemeLinks', '', 'A gateway to philosophy sites, this one offers especially rich access to full text sources, retrievable by author, title, and subject searches. Maintained by Thomas Ryan Stone, a computer specialist with philosophy background.', '', NULL, '2013-08-09 15:41:48', 0),
(350, 'Internet Encyclopedia of Philosophy', '', 'Ongoing, cooperative encyclopedia maintained by professional philosophers. Topics alphabetically arranged. Article authors are clearly identified.', '', NULL, '2013-08-09 15:45:42', 0),
(351, 'John Locke Resources', '', '<span class="title" style="color: rgb(204, 0, 0); font-size: 19px; font-style: italic; font-weight: bold; font-family: Garamond, Georgia, Times, serif; line-height: 22px; text-align: justify;">&quot;John Locke Resources</span><span style="color: rgb(0, 0, 0); font-family: Garamond, Georgia, Times, serif; font-size: 18px; line-height: 22px; text-align: justify;">&nbsp;is a gateway to information about the English philosopher John Locke (1632&ndash;1704) and to tools supporting scholarship on Locke.&quot;</span>', '', NULL, '2013-08-09 15:46:10', 0),
(352, 'Philosophy around the Web', '', 'Useful gateway, maintained with style and humor by Peter King, of Pembroke College (Oxford University), provides links to online reference tools, full text of primary sources, and other sites arranged by philosophical topic.', '', NULL, '2013-08-09 15:47:13', 0),
(353, 'Bach Bibliography', '', 'Bibliography of citations dealing with the music of Bach, compiled by Yo Tomita, School of Music &amp; Sonic Arts, Queen&rsquo;s University Belfast with the generous assistance of Nobuaki Ebata and contributors.', '', NULL, '2013-08-09 16:12:09', 0),
(354, 'Best Information on the Net: Music', '', 'Contains selected links on all types of music. By the library at St. Ambrose University.', '', NULL, '2013-08-09 16:12:43', 0),
(355, 'Broadside Verses Collection – Enoch Pratt Free Library', '', '&ldquo;The Broadside Verses provide a unique look into life in 19th century America, especially life in Maryland between 1860 and 1865, when the country was in turmoil, and immediately after the Civil War. Most of these verses have a political message, but others &ndash; advertisements, love songs, temperance songs, ballads about life and death &ndash; show what life was like for Marylanders and other Americans after the Civil War, providing insight into the American character and personality.&rdquo;', '', NULL, '2013-08-09 16:13:23', 0),
(356, 'Ethnomusicology – World Music – UW library', '', 'List with sections on organizations and institutions, publications, recordings, and sites by geographical region. From University of Washington Libraries; some material restricted to UW users.', '', NULL, '2013-08-09 16:14:28', 0),
(357, ' Hiphop Archive ', '', '&ldquo;The Hiphop Archive organizes and develops collections, initiates and participates in research activities, sponsors events and acquires material culture associated with Hiphop in the U.S. and throughout the world.&rdquo;', '', NULL, '2013-08-09 16:16:31', 0),
(358, 'IMSLP: Petrucci Music Library', '', 'The IMSLP/Petrucci Music Library is a wiki with the goal of creating a complete virtual library of all scores in the public domain. Not recommended for current research, but useful for a quick lookup, it has over 79,000 scores as of Dec. 2010. It includes works in the public domain as well as works by composers willing to share their music at no charge.', '', NULL, '2013-08-09 16:17:09', 0),
(359, 'Internet Archive', '', '3 million out of copyright texts, music, video and more!', '', NULL, '2013-08-09 16:18:04', 0),
(360, 'Voice of the Shuttle: Music', '', 'Includes general music sites as well as resources for the study of music by period, music theory and computer music. By Allan Liu, University of California &ndash; Santa Barbara.', '', NULL, '2013-08-09 16:18:30', 0),
(361, 'Library of Congress: Music', '', 'Links to digitized collections (audio and print) as well as information resources on musical topics provided by the U.S. Library of Congress.', '', NULL, '2013-08-09 16:20:41', 0),
(362, 'IMDB: Internet Movie Database', '', 'Popular directory of information about films and actors including plot synopses, reviews and credits.', '', NULL, '2013-08-09 16:26:23', 0),
(363, 'Art Renewal Center Museum', '', 'Anti-modernist collection of representational art and artists online. Useful for images of older representational art', '', NULL, '2013-08-12 14:52:13', 0),
(364, 'ATLA Cooperative Digital Resources Initiative (CDRI)', '', 'ATLA hosts collections of religion-related art pieces, manuscripts and letters, photographs, even postcards and shape-note music. Digital images from the libraries and special collections of ATLA member libraries, including: photographs, slides, papyri, coins, maps, postcards, manuscripts, sermons, art&hellip;', '', NULL, '2013-08-12 14:53:15', 0),
(365, 'Center for Creative Photography', '', 'The University of Arizona&rsquo;s Center for Creative Photography houses the archives of Ansel Adams, Edward Weston, Garry Winogrand, Harry Callahan, and other great twentieth-century photographers. Thousands of photographs are available for online study.', '', NULL, '2013-08-12 14:54:26', 0),
(366, 'Heilbrunn Timeline of Art History', '', 'From the Metropolitan Museum of Art', '', NULL, '2013-08-12 14:55:10', 0),
(367, 'Manuscriptorium: Building Virtual Research Environment for the Sphere of Historical Resources ', '', 'Access to digitized materials from the libraries of Europe.', '', NULL, '2013-08-12 14:58:59', 0),
(368, 'Metropolitan Museum of Art – Collections', '', 'Descriptive database covering large sections of the collections of the Metropolitan Museum of Art. For each item, may include photos and information such as Gallery label, additional views, provenance, exhibition history, references.', '', NULL, '2013-08-12 15:01:22', 0),
(369, 'Metropolitan Museum of Art — Publications Portal', '', 'MetPublications is a portal to the Met&rsquo;s publishing program. This resource will eventually offer access to nearly all books, Bulletins, and Journals published by the Metropolitan Museum, including online publications. Current titles may be previewed and fully searched online, with a link to purchase the book. The full contents of out of print titles may be read online, searched, or downloaded as a PDF, at no cost.', '', NULL, '2013-08-12 15:05:05', 0),
(370, 'Museum Directory', '', 'List of museums with online presences.', '', NULL, '2013-08-12 15:06:23', 0),
(371, 'National Gallery of Art: NGA Images', '', 'Repository of digital images from the collections of the National Gallery of Art.', '', NULL, '2013-08-12 15:07:40', 0);
