(466, 'TMR: The Medieval Review', '', 'Reviews of books related to medieval history and literature.', '', NULL, '2013-08-12 20:52:35', 0),
(467, 'Vivarium and OLIVER', '', 'Vivarium searches Hill Museum and Manuscript Library&#39;s online manuscript collection; OLIVER is a catalog of their manuscript holdings.<br />\r\nGo to Browse and choose Manuscripts or other collections to see what materials are available.', '', NULL, '2013-08-12 20:54:44', 0),
(468, 'Accueil de Persée', '', 'Indexes and provides full text (and archives) for approximately 30+ scientific journals in the humanities.', '', NULL, '2013-08-12 20:57:09', 0),
(469, 'ATHENA: Literature, Books', '', 'One of the largest collections of French literature in electronic form. Pierre Peroud.', '', NULL, '2013-08-12 20:57:38', 0),
(470, 'CESAR – Calendrier Electronique des Spectacles sous l’Ancien Régime', '', 'Links to theatrical entertainment in France during the 17th and 18th centuries.', '', NULL, '2013-08-12 20:58:06', 0),
(471, 'ClicNet', '', 'A guide to French literature and culture on the web, maintained by Swarthmore.', '', NULL, '2013-08-13 13:21:45', 0),
