(523, 'Political Graveyard: Search', '', '&ldquo;A database of political history and cemeteries, with brief biographical entries for 224,230 U.S. political figures, living and dead, from the 1700s to the present.&rdquo;', '', NULL, '2013-08-13 15:34:16', 0),
(524, 'Schomburg Center for Research in Black Culture', '', 'Digital Schomburg- Material from New York Public Library&rsquo;s premiere collection on African American history and literature.', '', NULL, '2013-08-13 15:35:30', 0),
