(713, ' Mapping History', '', 'Provides &quot;interactive and animated representations of fundamental historical problems and/or illustrations of historical events, developments, and dynamics.&quot; from the University of Oregon.', '', NULL, '2013-08-14 20:20:44', 0),
(714, 'Maps on the Web', '', 'From Oxford Digital Library.', '', NULL, '2013-08-14 20:22:27', 0),
(715, 'National Library of Ireland Digital Resources', '', 'Manuscripts and visual resources from the collections of the National Library of Ireland.', '', NULL, '2013-08-14 20:46:24', 0),
(716, 'NixonTapes.org', '', 'The site is a comprehensive and user-friendly collection of the secretly-recorded tapes made by President Richard Nixon between 1971 and 1973. The site currently offers all of the recordings that have been de-classified and released by the National Archives and Records Administration (NARA). Transcriptions for many of the tapes are available, enabling the user to both listen and read the conversations at the same time.', '', NULL, '2013-08-14 20:49:01', 0),
(717, 'Not Even Past', '', '&quot;Dynamic, accessible, short articles on every field of History...&nbsp; developed by the Department of History at the University of Texas at Austin.&quot;', '', NULL, '2013-08-14 20:51:03', 0),
(718, ' Office of the Historian, Department of State', '', 'Historical documents and history of the Department of State.', '', NULL, '2013-08-14 20:52:12', 0),
(719, 'Older Periodicals — How to Find them in Drew Library', '', 'Lists of 18th and 19th Century journals owned by Drew Library. Also provides links to online archival journal collections.', '', NULL, '2013-08-14 20:52:47', 0),
(720, 'Open Jurist', '', '&quot;Access to published United States legal opinions, Code and legal resources without charge. &quot;', '', NULL, '2013-08-14 20:53:52', 0),
(721, 'Our Documents: 100 Milestone Documents', '', '&quot;A list of 100 milestone documents, compiled by the National Archives and Records Administration, and drawn primarily from its nationwide holdings. The documents chronicle United States history from 1776 to 1965.&quot;', '', NULL, '2013-08-14 20:54:45', 0),
(722, 'Pittsburgh and Beyond: The Experience of the Jewish Community', '', '&quot;National Council of Jewish Women Oral History Collection at the University of Pittsburgh.&quot;', '', NULL, '2013-08-14 20:55:38', 0),
(723, 'Scripps Library and Multimedia Archive', '', 'The Scripps Library and Multimedia Archive&rsquo;s collection focuses on American politics and history with special attention paid to the American presidency.', '', NULL, '2013-08-14 20:57:57', 0),
(724, 'Shots of War: Photojournalism During the Spanish Civil War', '', 'Photos and information.', '', NULL, '2013-08-14 21:00:28', 0),
(725, 'State of the News Media', '', 'An annual report on American Journalism.', '', NULL, '2013-08-14 21:33:36', 0),
(726, 'National Archives – The Cabinet Papers 1915 – 1977', '', 'UK Cabinet papers and documents.', '', NULL, '2013-08-14 21:35:07', 0),
(727, 'The Telegraph: World War 2', '', 'Telegraph articles on World War 2 from 1939, historical pictures and video and the latest news on the 70th anniversary of the outbreak of World War 2.', '', NULL, '2013-08-14 21:36:20', 0),
(728, 'Timelines: Sources from History', '', '&quot;This British Library timeline allows you to explore collection items chronologically, from medieval times to the present day. It includes a diverse combination of texts: those that allow glimpses of everyday life (handbills, posters, letters, diaries), remnants of political events (charters, speeches, campaign leaflets), and the writings of some of our best known historical and literary figures.&quot;', '', NULL, '2013-08-14 21:37:49', 0),
(729, 'U.S. Congressional Serial Set Inventory', '', 'What libraries have which volumes of the U.S. Congressional Serial Set?', '', NULL, '2013-08-14 21:40:06', 0),
(730, 'Wilson Center Programs', '', 'Find analysis and primary sources on a variety of topics, including the Cold War, Congress, Urban Studies, North Korea, Global Health and Energy, and more.', '', NULL, '2013-08-14 21:41:55', 0),
(731, 'World War I Document Archive', '', '&quot;This archive of primary documents from World War One has been assembled by volunteers of the World War I Military History List (WWI-L).&quot;', '', NULL, '2013-08-14 21:42:54', 0),
(732, 'WWW-VL History Central Catalogue', '', 'Oldest index to history materials online (since 1993). Provides the Central Catalogue for an integrated and international history network, a substantive resource for both students and scholars. U. of Kansas.', '', NULL, '2013-08-14 21:43:40', 0),
(733, 'Business &amp; Human Rights', '', '&quot;Tracking the positive and negative impacts of over 5,100 companies worldwide&quot;', '', NULL, '2013-08-16 14:11:13', 0),
(734, 'C-Span Video Library', '', '&quot;The C-SPAN Archives records, indexes, and archives all C-SPAN programming for historical, educational, research, and archival uses. Every C-SPAN program aired since 1987, totaling over 201,000 hours, is contained in the C-SPAN Archives and immediately accessible through the database and electronic archival systems developed and maintained by the C-SPAN Archives.&quot;', '', NULL, '2013-08-16 14:16:45', 0),
(735, 'Center for the Study of the Public Domain', '', '<span style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; line-height: normal; background-color: rgb(222, 219, 190);">&quot;The Center for the Study of the Public Domain at Duke Law School.... founded in September of 2002, as part of the school&#39;s wider&nbsp;</span><a href="http://web.law.duke.edu/ip/" style="color: rgb(0, 0, 102); font-family: Verdana, Arial, Helvetica, sans-serif; line-height: normal; background-color: rgb(222, 219, 190);">intellectual property program</a><span style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; line-height: normal; background-color: rgb(222, 219, 190);">, its mission is to promote research and scholarship on the contributions of the public domain to speech, culture, science and innovation, to promote debate about the balance needed in our intellectual property system and to translate academic research into public policy solutions.&quot;&nbsp;</span>', '', NULL, '2013-08-16 14:29:43', 0),
(736, 'Comparative Constitutions Project', '', '<span style="color: rgb(44, 104, 133); font-family: Tahoma, Verdana, Arial, Helvetica, sans-serif; font-size: 11px; line-height: normal; text-align: justify; background-color: rgb(168, 201, 218);">&quot;The intent of the project is to investigate the sources and consequences of constitutional choices. Towards this end, the investigators are collecting data on the formal characteristics of written constitutions, both current and historical, for most independent states since 1789. The data will ultimately be available in its entirety to the public.&quot;</span>', '', NULL, '2013-08-16 14:42:05', 0),
(737, 'Congress Votes Database', '', 'Excellent resource for voting records of Congress, from the Washington Post.', '', NULL, '2013-08-16 14:58:40', 0),
(738, 'CQ RollCall Politics', '', '&quot;The source for news on Capital Hill since 1955.&quot;', '', NULL, '2013-08-16 15:01:22', 0),
(739, 'CIRN -  Child Rights International Network', '', '<span style="color: rgb(64, 64, 64); font-family: ''Lucida Sans Unicode'', ''Lucida Grande'', Georgia, Arial, Helvetica, ''Times New Roman'', Times, serif; line-height: 18px;">Guided by our passion for social and legal change, CRIN is building a global network for children&#39;s rights. We press for rights, not charity, and advocate for a genuine systemic shift in how governments and societies view children.&nbsp;</span><br style="margin: 0px; padding: 0px; color: rgb(64, 64, 64); font-family: ''Lucida Sans Unicode'', ''Lucida Grande'', Georgia, Arial, Helvetica, ''Times New Roman'', Times, serif; line-height: 18px;" />\r\n<br style="margin: 0px; padding: 0px; color: rgb(64, 64, 64); font-family: ''Lucida Sans Unicode'', ''Lucida Grande'', Georgia, Arial, Helvetica, ''Times New Roman'', Times, serif; line-height: 18px;" />\r\n<span style="color: rgb(64, 64, 64); font-family: ''Lucida Sans Unicode'', ''Lucida Grande'', Georgia, Arial, Helvetica, ''Times New Roman'', Times, serif; line-height: 18px;">Our inspiration is the United Nations Convention on the Rights of the Child (CRC), which we use to bring children&#39;s rights to the top of the international agenda. We launch advocacy campaigns, lead international children&#39;s rights coalitions, and strive to make existing human rights enforcement mechanisms accessible for all.&nbsp;</span><br style="margin: 0px; padding: 0px; color: rgb(64, 64, 64); font-family: ''Lucida Sans Unicode'', ''Lucida Grande'', Georgia, Arial, Helvetica, ''Times New Roman'', Times, serif; line-height: 18px;" />\r\n<br style="margin: 0px; padding: 0px; color: rgb(64, 64, 64); font-family: ''Lucida Sans Unicode'', ''Lucida Grande'', Georgia, Arial, Helvetica, ''Times New Roman'', Times, serif; line-height: 18px;" />\r\n<span style="color: rgb(64, 64, 64); font-family: ''Lucida Sans Unicode'', ''Lucida Grande'', Georgia, Arial, Helvetica, ''Times New Roman'', Times, serif; line-height: 18px;">More than 2,100 organisations in 150 countries rely on CRIN&#39;s publications, research and information.&nbsp;</span>', '', NULL, '2013-08-16 15:03:18', 0),
(740, 'Encyclo', '', '<strong style="color: rgb(76, 76, 76); font-family: ff-tisa-web-pro-1, ff-tisa-web-pro-2, ''Lucida Grande'', serif; font-size: 16px; line-height: 24px; background-color: rgb(244, 241, 236);">&quot;Encyclo is an encyclopedia of the future of news</strong><span style="color: rgb(76, 76, 76); font-family: ff-tisa-web-pro-1, ff-tisa-web-pro-2, ''Lucida Grande'', serif; font-size: 16px; line-height: 24px; background-color: rgb(244, 241, 236);">, produced by the&nbsp;</span><a href="http://www.niemanlab.org/" style="outline: none; color: rgb(128, 0, 0); font-family: ff-tisa-web-pro-1, ff-tisa-web-pro-2, ''Lucida Grande'', serif; font-size: 16px; line-height: 24px; background-color: rgb(244, 241, 236);">Nieman Journalism Lab</a><span style="color: rgb(76, 76, 76); font-family: ff-tisa-web-pro-1, ff-tisa-web-pro-2, ''Lucida Grande'', serif; font-size: 16px; line-height: 24px; background-color: rgb(244, 241, 236);">&nbsp;at Harvard University.&quot;</span>', '', NULL, '2013-08-16 15:12:56', 0),
(741, 'European Commission - Public Opinion', '', '<span style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, ''DejaVu Sans'', sans-serif; font-size: x-small; line-height: 12px;">&quot;This is the website for the Public Opinion Analysis sector of the European Commission. Since 1973, the European Commission has been monitoring the evolution of public opinion in the Member States, thus helping the preparation of texts, decision-making and the evaluation of its work. Our surveys and studies address major topics concerning European citizenship: enlargement, social situation, health, culture, information technology, environment, the Euro, defence, etc.&quot;</span>', '', NULL, '2013-08-16 15:15:22', 0),
(742, 'Delegation of the European Union to the United States', '', '<span style="color: rgb(28, 32, 34); font-family: Arial, ''Helvetica CY'', ''Nimbus Sans L'', sans-serif; font-size: 15px; line-height: 18px;">&quot;The European Union is a unique economic and political partnership among 28 diverse democracies united in their commitment to peace, democracy, the rule of law, and respect for human rights. It is represented in the United States by the Washington, DC Delegation of the European Union, a full-fledged diplomatic mission working in close coordination with the Embassies and Consulates of the 28 EU Member States.&quot;</span>', '', NULL, '2013-08-16 15:17:13', 0),
(743, 'European Union Internet Resources ', '', 'From the University of California Berkeley Library.', '', NULL, '2013-08-16 15:19:18', 0),
(744, 'Resources on Faith, Ethics, and Public Life', '', '<span style="font-family: Georgia, Verdana, Arial; font-size: 14px; line-height: 19px; background-color: rgb(243, 243, 243);">&quot;Interest in the intersection of religion and international affairs is growing around the world, and this database serves as a companion resource to the recently released edited volume</span><em style="font-family: Georgia, Verdana, Arial; font-size: 14px; line-height: 19px; background-color: rgb(243, 243, 243);">Rethinking Religion and World Affairs</em><span style="font-family: Georgia, Verdana, Arial; font-size: 14px; line-height: 19px; background-color: rgb(243, 243, 243);">, one of the first comprehensive guides to this complex topic. This page also includes links to the activities sponsored by the Henry R. Luce Initiative on Religion and International Affairs and to other networks of organizations and programs that address religion, society,...&quot;</span>', '', NULL, '2013-08-16 15:34:55', 0),
(745, 'Federal Judicial  Center', '', '&quot;Education and research agency for the Federal courts.&quot;', '', NULL, '2013-08-16 15:38:23', 0),
(746, 'Federal Register', '', '&quot;The &nbsp;Daily Journal of the US Government.&quot;', '', NULL, '2013-08-16 15:39:49', 0),
(747, 'Follow the Money: National Institute on Money in State Politics', '', '<span style="font-family: verdana, arial, helvetica, sans-serif; font-size: 13px; line-height: 16px; background-color: rgb(251, 251, 251);">&quot;Money in state politics plays a pivotal role in shaping public policy in individual states and across the nation. We track political donations in all 50 states.&quot;</span>', '', NULL, '2013-08-16 15:43:23', 0),
(748, 'Foreign  Consular Offices in the United States', '', '<span style="color: rgb(37, 37, 37); font-family: ''Lucida Grande'', Arial, Verdana, sans-serif; line-height: 20px;">&quot;This publication contains a complete and official listing of the foreign consular offices in the United States, and recognized consular officers.&nbsp; Compiled by the U.S. Department of State, with the full cooperation of the foreign missions in Washington...&quot;</span>', '', NULL, '2013-08-16 15:45:58', 0);
