(1171, 'How to access ScienceDirect articles', 'ScienceDirect articles', 'Detailed instructions about obtaining articles from ScienceDirect. Drew students can get &quot;pay-per-download&quot; ScienceDirect articles through the Library, either by Inter-Library Loan or using Library Chat.&nbsp;', '', NULL, '2015-02-03 20:03:11', 0),
(1172, 'Citation Searching in Web of Science', '', 'Citation searching is a way to &quot;look forward&quot; from the starting point of a particular article. You may use this strategy instead of keyword searching to find more articles on the same (or similar) topics.&nbsp;', '', NULL, '2015-02-03 20:49:27', 0),
(1173, 'Searching Google Scholar', 'Google Scholar Tips', 'Useful information about searching Google Scholar. Its strengths, weaknesses, features, and benefits can be found here.&nbsp;', '', NULL, '2015-02-03 20:59:45', 0),
(1174, 'Get full text in PubMed', '', 'Sometimes PubMed makes it a bit more complicated to get full text. Instructions about how to go about obtaining it can be found here.&nbsp;', '', NULL, '2015-02-03 21:04:59', 0),
