(1199, 'Encyclopedia of LGBTQ Literature, 2009.', '', 'Information on selected authors and concepts, with recommended further reading.', '', NULL, '2015-03-31 16:48:32', 0),
(1200, 'DeStatis - Federal Statistical Office of Germany', '', '<span style="color: rgb(43, 43, 45); font-family: Arial, Helvetica, sans-serif; line-height: 17.0004005432129px;">&quot;Our mission is to provide and disseminate statistical information which has to be objective, independent and of high quality. Our federal statistics are available to everybody: politicians, authorities, businesses and citizens.&quot;</span>', '', NULL, '2015-04-14 19:23:19', 0),
(1201, 'The Illustrated First World War ', '', '<p style="margin: 0px 0px 1em; padding: 0px; border: 0px; font-family: lato, sans-serif; font-size: 0.95em; font-stretch: inherit; line-height: 1.5em; vertical-align: baseline; color: rgb(119, 119, 119);">&quot;With the centenary of the First World War upon us, ILN Ltd, the custodians of the celebrated&nbsp;<em style="margin: 0px; padding: 0px; border: 0px; font-family: inherit; font-size: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline;">Illustrated London News</em>&nbsp;and&nbsp;<em style="margin: 0px; padding: 0px; border: 0px; font-family: inherit; font-size: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline;">Great Eight</em>&nbsp;Illustrated Magazine collection archives, felt a responsibility to make the 1914-18 archives available to the public for research,<a href="http://www.illustratedfirstworldwar.com/teaching-resources/" style="margin: 0px; padding: 0px; border: 0px; font-family: inherit; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; color: rgb(191, 152, 73); text-decoration: none;" title="Teaching">&nbsp;education</a>&nbsp;and pleasure. With the help of a grant from the Heritage Lottery Fund, The Illustrated First World War website was created.&quot;</p>\r\n\r\n<p style="margin: 0px 0px 1em; padding: 0px; border: 0px; font-family: lato, sans-serif; font-size: 0.95em; font-stretch: inherit; line-height: 1.5em; vertical-align: baseline; color: rgb(119, 119, 119);">&nbsp;</p>\r\n', '', NULL, '2015-04-14 19:37:32', 0),
(1202, 'Measuring Worth', '', '<span style="color: rgb(51, 51, 0); font-family: Verdana, sans-serif; line-height: 20px;">&quot;There are two missions of this site. The&nbsp;</span><em style="color: rgb(51, 51, 0); font-family: Verdana, sans-serif; line-height: 20px;">first</em><span style="color: rgb(51, 51, 0); font-family: Verdana, sans-serif; line-height: 20px;">&nbsp;is to make available to the public the highest quality and most reliable historical data on important economic aggregates, with particular emphasis on &quot;nominal (current-price) measures, as well as real (constant-price) measures. The data presented here on the United States, United Kingdom and Australia, have been created using the highest standards of the fields of economics and history, and they were rigorously refereed by the most distinguished researchers in the fields. The&nbsp;</span><em style="color: rgb(51, 51, 0); font-family: Verdana, sans-serif; line-height: 20px;">second</em><span style="color: rgb(51, 51, 0); font-family: Verdana, sans-serif; line-height: 20px;">&nbsp;is to provide carefully designed compartors (using these data) that explain the many issues involved in making value comparison over time.&quot;</span>', '', NULL, '2015-04-14 19:40:14', 0),
(1203, 'Scotusblog -Supreme Court of the United States Blog', '', 'Official blog of the United States Supreme Court', '', NULL, '2015-04-14 19:52:29', 0),
(1204, 'The Global Think Tank Directory', '', '<span style="color: rgb(8, 13, 18); font-family: Arimo, HelveticaNeue, ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px; line-height: 21.4500007629395px; text-align: center; background-color: rgb(252, 252, 252);">&quot;The&nbsp;</span><a href="http://gotothinktank.com/about-us/" style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px rgb(225, 225, 225); font-family: Arimo, HelveticaNeue, ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px; font-stretch: inherit; line-height: 21.4500007629395px; vertical-align: baseline; outline: 0px; max-width: 100%; color: rgb(192, 95, 95); text-align: center; background-color: rgb(252, 252, 252);" title="About Us"><strong style="box-sizing: border-box; margin: 0px; padding: 0px; border: 0px rgb(225, 225, 225); font-family: inherit; font-size: inherit; font-style: inherit; font-variant: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline;">Think Tanks and Civil Societies Program (TTCSP</strong>)&nbsp;</a><span style="color: rgb(8, 13, 18); font-family: Arimo, HelveticaNeue, ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px; line-height: 21.4500007629395px; text-align: center; background-color: rgb(252, 252, 252);">at the Lauder Institute, University of Pennsylvania conducts research on the role policy institutes play in governments and in civil societies around the world.&quot;</span>', '', NULL, '2015-04-14 20:00:54', 0),
(1205, 'Union Membership and Coverage Database from the CPS', '', '<span style="color: rgb(0, 0, 0); font-family: Arial, sans-serif; font-size: 13.3333330154419px; line-height: normal;">&quot;The&nbsp;</span><b style="color: rgb(0, 0, 0); font-family: Arial, sans-serif; font-size: 13.3333330154419px; line-height: normal;"><i>Union Membership and Coverage Database</i></b><i style="color: rgb(0, 0, 0); font-family: Arial, sans-serif; font-size: 13.3333330154419px; line-height: normal;">,&nbsp;</i><span style="color: rgb(0, 0, 0); font-family: Arial, sans-serif; font-size: 13.3333330154419px; line-height: normal;">available at&nbsp;</span><b style="color: rgb(0, 0, 0); font-family: Arial, sans-serif; font-size: 13.3333330154419px; line-height: normal;"><i>www.unionstats.com</i></b><span style="color: rgb(0, 0, 0); font-family: Arial, sans-serif; font-size: 13.3333330154419px; line-height: normal;">, is an Internet data resource providing private and public sector labor union membership, coverage, and density estimates compiled from the monthly household Current Population Survey (CPS) using BLS methods.</span><span style="color: rgb(0, 0, 0); font-family: Arial, sans-serif; font-size: 13.3333330154419px; line-height: normal;">&nbsp;&nbsp;</span><span style="color: rgb(0, 0, 0); font-family: Arial, sans-serif; font-size: 13.3333330154419px; line-height: normal;">Economy-wide estimates are provided beginning in 1973; estimates by state, detailed industry, and detailed occupation begin in 1983; and estimates by metropolitan area begin in 1986.</span><span style="color: rgb(0, 0, 0); font-family: Arial, sans-serif; font-size: 13.3333330154419px; line-height: normal;">&nbsp;&nbsp;</span><span style="color: rgb(0, 0, 0); font-family: Arial, sans-serif; font-size: 13.3333330154419px; line-height: normal;">The&nbsp;</span><b style="color: rgb(0, 0, 0); font-family: Arial, sans-serif; font-size: 13.3333330154419px; line-height: normal;"><i>Database</i>,<i>&nbsp;</i></b><span style="color: rgb(0, 0, 0); font-family: Arial, sans-serif; font-size: 13.3333330154419px; line-height: normal;">constructed by&nbsp;</span><b style="color: rgb(0, 0, 0); font-family: Arial, sans-serif; font-size: 13.3333330154419px; line-height: normal;">Barry Hirsch&nbsp;</b><span style="color: rgb(0, 0, 0); font-family: Arial, sans-serif; font-size: 13.3333330154419px; line-height: normal;">(Andrew Young School of Policy Studies, Georgia State University) and&nbsp;</span><b style="color: rgb(0, 0, 0); font-family: Arial, sans-serif; font-size: 13.3333330154419px; line-height: normal;">David Macpherson&nbsp;</b><span style="color: rgb(0, 0, 0); font-family: Arial, sans-serif; font-size: 13.3333330154419px; line-height: normal;">(Department of Economics, Trinity University), was created in 2002 and is updated annually.&quot;</span>', '', NULL, '2015-04-14 20:02:53', 0),
(1206, ' Visual History Archive Online (VHA Online)', '', '<span style="color: rgb(48, 48, 48); font-family: Arial, Helvetica, sans-serif; font-size: 17px; line-height: 22.1000003814697px;">&quot;Welcome to the Visual History Archive Online (VHA Online) &ndash; an online portal from USC Shoah Foundation that allows users to search through and view nearly 52,000 audiovisual testimonies of survivors and witnesses of the Holocaust and other genocides. These testimonies were conducted in 58 countries and in 34 languages.&quot; <strong>You must register to use this site.</strong></span>', '', NULL, '2015-04-14 20:05:01', 0),
(1207, 'The Crisis', '', '<span style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: normal; text-align: -webkit-center;">An African American Periodical. Library has in print: v 52 1945 - v 54 1947,v 65 1958 - v 73 1966<br />\r\nSome articles from this magazine are also available online from the&nbsp;<a href="http://bsc.chadwyck.com.ezproxy.drew.edu/home/home.do" target="_blank">Black Studies Center Database.</a></span>', '', NULL, '2015-04-15 13:54:09', 0),
(1208, 'The Crusader', '', 'African American Periodical. The Library owns in print:&nbsp;<span style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: medium; line-height: normal;">Issues for Sept. 1918-Oct. 1920 lack whole number but constitute whole no. 1-26.</span><br style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: medium; line-height: normal;" />\r\n<span style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: medium; line-height: normal;">Reprint lacks v. 5, no. 1 (Sept. 1921).</span>', '', NULL, '2015-04-15 14:00:40', 0),
(1209, 'Ebony', '', 'African American Magazine.&nbsp;<span style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: normal; text-align: -webkit-center;">Microfilm: v 10 (1954) - v 69, Iss. 1-12 (Oct 2014) 2013 - current in print also.</span>', '', NULL, '2015-04-15 14:08:01', 0),
(1210, 'Optical Society of America (OSA) Publishing', 'OSA', 'OSA Publishing (formerly Optics InfoBase)&nbsp;offers research about optics and photonics. The website interface provides users with features including full-text search and tools to refine content searches by date, publication and author. From the Optical Society, a&nbsp;professional&nbsp;society&nbsp;of&nbsp;optical&nbsp;engineers and scientists.<br />\r\n&nbsp;', '', NULL, '2015-04-29 23:11:18', 0),
(1211, 'NIOSH Pocket Guide to Chemical Hazards', '', '<span id="docs-internal-guid-1e05f3a5-7c4e-52fa-c950-23af276694f6"><span style="font-size: 15px; font-family: Arial; vertical-align: baseline; white-space: pre-wrap; background-color: rgb(255, 255, 255);">&quot;The NIOSH Pocket Guide to Chemical Hazards is intended as a source of general industrial hygiene information on several hundred chemicals/classes for workers, employers, and occupational health professionals. The NIOSH Pocket Guide does not contain an analysis of all pertinent data, rather it presents key information and data in abbreviated or tabular form for chemicals or substance groupings (e.g. cyanides, fluorides, manganese compounds) that are found in the work environment. The information found in the NIOSH Pocket Guide should help users recognize and control occupational chemical hazards.&quot; - from the Centers for Disease Control website. Published by the National Institute for Occupational Safety and Health (NIOSH).</span></span>', '', NULL, '2015-05-22 15:50:06', 0),
