(1217, 'World Health Organization: Avian influenza in humans', '', 'This page by the intergovernmental World Health Organization draws together information about avian flu research. Looking under &quot;H5N1 Research Issues&quot; you will find both&nbsp;<strong>FAQs: H5N1 Influenze</strong> and&nbsp;<strong>FAQs: H5N1 research issues</strong>.&nbsp;', '', NULL, '2015-07-15 15:45:52', 0),
(1218, 'Medline Plus page on Bird Flu', '', 'Put together by the U.S. National Library of Medicine, this page is a great place to start for information on the various strains of Influenze. It includes basic information, news, statistics and research, and even links to descriptions of scholarly journal articles.', '', NULL, '2015-07-15 15:54:42', 0),
(1219, 'Brooklyn Historical Society ', '', 'Archives and Special Collections at the Brooklyn Historical Society. Many collections are digitized and browseable by subject.', '', NULL, '2015-07-16 16:08:06', 0),
(1220, 'Online Library of Liberty, from the Liberty Fund', '', '<span style="color: rgb(77, 77, 77); font-family: Georgia, ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px;">&quot;Liberty Fund, Inc. is a private, educational foundation established in 1960 to encourage the study of the ideal of a society of free and responsible individuals. The Foundation develops, supervises, and finances its own educational activities to foster thought and encourage discourse on enduring intellectual issues pertaining to liberty.&quot;&nbsp;</span>', '', NULL, '2015-07-16 16:13:28', 0),
(1221, 'New Jersey Teacher Evaluations 2014-2015', '', '<span style="font-family: ''Open Sans'', sans-serif; font-size: 14px; line-height: 20px;">&quot;This database shows the number of teachers each school who were rated in the following performance categories: Ineffective, Partially Effective, Effective and Highly Effective.&quot;</span>', '', NULL, '2015-07-17 13:48:28', 0),
(1222, 'Kids Count Data Center', '', '<span style="font-family: ''Trebuchet MS'', verdana; font-size: 16px; line-height: 20px; background-color: rgb(239, 240, 240);">&quot;A project of the Annie E. Casey Foundation, KIDS COUNT is the premier source for data on child and family well-being in the United States. Access hundreds of indicators, download data and create reports and graphics on the KIDS COUNT Data Center that support smart decisions about children and families.&quot;</span>', '', NULL, '2015-07-27 16:49:17', 0),
